import { GoogleGenAI, Type } from "@google/genai";

const ai = new GoogleGenAI({ apiKey: process.env.API_KEY || '' });

export const analyzeCompliance = async (providerName: string, employeesData: string) => {
  try {
    const response = await ai.models.generateContent({
      model: 'gemini-3-flash-preview',
      contents: `Analise a conformidade de segurança para o fornecedor "${providerName}" em uma usina de biometano. 
      Dados dos funcionários: ${employeesData}. 
      Gere um resumo curto destacando riscos imediatos, treinamentos vencidos críticos e uma recomendação para o Técnico de Segurança.`,
      config: {
        systemInstruction: "Você é um especialista em Segurança do Trabalho com foco em Indústrias de Biogás e Biometano (NR-20, NR-13, NR-33). Forneça insights práticos e diretos.",
      }
    });
    return response.text;
  } catch (error) {
    console.error("Gemini Analysis Error:", error);
    return "Não foi possível realizar a análise inteligente no momento.";
  }
};

export const suggestTrainingPlan = async (jobDescription: string) => {
  try {
    const response = await ai.models.generateContent({
      model: 'gemini-3-flash-preview',
      contents: `O funcionário terá a seguinte função: ${jobDescription}. Liste as NRs e cursos obrigatórios necessários para trabalhar em uma usina de biometano.`,
      config: {
        responseMimeType: "application/json",
        responseSchema: {
          type: Type.OBJECT,
          properties: {
            requiredNRs: {
              type: Type.ARRAY,
              items: { type: Type.STRING }
            },
            recommendations: { type: Type.STRING }
          },
          required: ["requiredNRs", "recommendations"]
        }
      }
    });
    return JSON.parse(response.text || '{}');
  } catch (error) {
    console.error("Gemini Plan Error:", error);
    return null;
  }
};

import React from 'react';
