import React from 'react';
import { Users, FileX, Clock, CheckCircle2, Factory, TrendingUp } from 'lucide-react';
import { Provider } from '../types';

interface DashboardProps {
  providers: Provider[];
}

const Dashboard: React.FC<DashboardProps> = ({ providers }) => {
  const stats = React.useMemo(() => {
    let totalEmployees = 0;
    let expired = 0;
    let expiring = 0;

    providers.forEach(p => {
      p.employees.forEach(e => {
        totalEmployees++;
        e.documents.forEach(d => {
          if (d.status === 'EXPIRED') expired++;
          if (d.status === 'EXPIRING') expiring++;
        });
      });
    });

    return { totalEmployees, expired, expiring };
  }, [providers]);

  const cards = [
    { label: 'Fornecedores', value: providers.length, icon: Factory, color: 'text-blue-600', bg: 'bg-blue-50' },
    { label: 'Colaboradores', value: stats.totalEmployees, icon: Users, color: 'text-indigo-600', bg: 'bg-indigo-50' },
    { label: 'Docs Vencidos', value: stats.expired, icon: FileX, color: 'text-red-600', bg: 'bg-red-50' },
    { label: 'A Vencer (30d)', value: stats.expiring, icon: Clock, color: 'text-amber-600', bg: 'bg-amber-50' },
  ];

  return (
    <div className="space-y-8">
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
        {cards.map((card, i) => {
          const Icon = card.icon;
          return (
            <div key={i} className="bg-white p-6 rounded-2xl border border-slate-200 shadow-sm hover:shadow-md transition-shadow">
              <div className="flex items-center justify-between mb-4">
                <div className={`${card.bg} p-3 rounded-xl`}>
                  <Icon className={`w-6 h-6 ${card.color}`} />
                </div>
                <div className="text-emerald-500 bg-emerald-50 px-2 py-1 rounded text-xs font-bold flex items-center gap-1">
                  <TrendingUp className="w-3 h-3" />
                  +12%
                </div>
              </div>
              <h3 className="text-slate-500 text-sm font-medium">{card.label}</h3>
              <p className="text-3xl font-bold text-slate-900 mt-1">{card.value}</p>
            </div>
          );
        })}
      </div>

      <div className="grid grid-cols-1 lg:grid-cols-3 gap-8">
        <div className="lg:col-span-2 bg-white rounded-2xl border border-slate-200 p-6 shadow-sm">
          <h3 className="text-lg font-bold mb-6 flex items-center gap-2">
            <CheckCircle2 className="w-5 h-5 text-emerald-500" />
            Empresas com Maior Risco de Segurança
          </h3>
          <div className="space-y-4">
            {providers.map(p => {
              const expiredCount = p.employees.reduce((acc, emp) => acc + emp.documents.filter(d => d.status === 'EXPIRED').length, 0);
              return (
                <div key={p.id} className="flex items-center justify-between p-4 rounded-xl border border-slate-100 hover:bg-slate-50 transition-colors">
                  <div>
                    <p className="font-semibold text-slate-800">{p.name}</p>
                    <p className="text-xs text-slate-500">{p.cnpj}</p>
                  </div>
                  <div className="flex items-center gap-4">
                    <div className="text-right">
                      <p className={`text-sm font-bold ${expiredCount > 0 ? 'text-red-600' : 'text-emerald-600'}`}>
                        {expiredCount} docs pendentes
                      </p>
                      <div className="w-32 h-2 bg-slate-100 rounded-full mt-1 overflow-hidden">
                        <div 
                          className={`h-full ${expiredCount > 0 ? 'bg-red-500' : 'bg-emerald-500'}`} 
                          style={{ width: `${Math.min(100, (expiredCount / 5) * 100)}%` }}
                        ></div>
                      </div>
                    </div>
                  </div>
                </div>
              );
            })}
          </div>
        </div>

        <div className="bg-emerald-900 rounded-2xl p-6 text-white shadow-xl flex flex-col justify-between">
          <div>
            <h3 className="text-xl font-bold mb-2">Dica de Segurança</h3>
            <p className="text-emerald-100 text-sm leading-relaxed">
              O biometano é altamente inflamável (NR-20). Certifique-se de que todos os terceiros possuam integração específica sobre os pontos de purga e ventilação da planta.
            </p>
          </div>
          <div className="mt-8 bg-emerald-800/50 p-4 rounded-xl border border-emerald-700">
            <p className="text-xs uppercase font-bold text-emerald-300 tracking-wider">Lembrete NR-33</p>
            <p className="text-sm mt-1 text-emerald-50">Exija o PET (Permissão de Entrada e Trabalho) para limpeza de biodigestores.</p>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Dashboard;

import React, { useState } from 'react';
import { Plus, Search, ChevronRight, UserPlus, FileText, Bot, AlertCircle } from 'lucide-react';
import { Provider, Employee, DocStatus } from '../types';
import { STATUS_COLORS, NR_LIST } from '../constants';
import { analyzeCompliance } from '../services/geminiService';

interface ProviderListProps {
  providers: Provider[];
  onAddProvider: () => void;
  onViewEmployee: (emp: Employee) => void;
}

const ProviderList: React.FC<ProviderListProps> = ({ providers, onAddProvider, onViewEmployee }) => {
  const [searchTerm, setSearchTerm] = useState('');
  const [aiAnalysis, setAiAnalysis] = useState<string | null>(null);
  const [isAnalyzing, setIsAnalyzing] = useState(false);

  const filteredProviders = providers.filter(p => 
    p.name.toLowerCase().includes(searchTerm.toLowerCase()) || 
    p.cnpj.includes(searchTerm)
  );

  const handleAiAnalysis = async (provider: Provider) => {
    setIsAnalyzing(true);
    const dataStr = provider.employees.map(e => `${e.name} (${e.role}): ${e.documents.map(d => `${d.type}-${d.status}`).join(', ')}`).join('; ');
    const result = await analyzeCompliance(provider.name, dataStr);
    setAiAnalysis(result || null);
    setIsAnalyzing(false);
  };

  return (
    <div className="space-y-6">
      <div className="flex flex-col md:flex-row gap-4 items-center justify-between bg-white p-4 rounded-2xl border border-slate-200 shadow-sm">
        <div className="relative w-full md:w-96">
          <Search className="absolute left-3 top-1/2 -translate-y-1/2 w-5 h-5 text-slate-400" />
          <input 
            type="text"
            placeholder="Buscar por nome ou CNPJ..."
            className="w-full pl-10 pr-4 py-2 bg-slate-50 border border-slate-200 rounded-xl focus:ring-2 focus:ring-emerald-500 focus:outline-none transition-all"
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
          />
        </div>
        <button 
          onClick={onAddProvider}
          className="w-full md:w-auto flex items-center justify-center gap-2 bg-emerald-600 hover:bg-emerald-700 text-white px-6 py-2.5 rounded-xl font-semibold transition-all shadow-md active:scale-95"
        >
          <Plus className="w-5 h-5" />
          Novo Prestador
        </button>
      </div>

      <div className="grid grid-cols-1 gap-6">
        {filteredProviders.map(provider => (
          <div key={provider.id} className="bg-white rounded-2xl border border-slate-200 shadow-sm overflow-hidden group">
            <div className="p-6 border-b border-slate-100 flex items-center justify-between">
              <div className="flex items-center gap-4">
                <div className="w-12 h-12 bg-slate-100 rounded-xl flex items-center justify-center font-bold text-slate-500">
                  {provider.name.charAt(0)}
                </div>
                <div>
                  <h3 className="text-lg font-bold text-slate-800">{provider.name}</h3>
                  <p className="text-sm text-slate-500 flex items-center gap-2">
                    {provider.cnpj} • {provider.contactEmail}
                  </p>
                </div>
              </div>
              <div className="flex items-center gap-3">
                <button 
                  onClick={() => handleAiAnalysis(provider)}
                  disabled={isAnalyzing}
                  className="flex items-center gap-2 text-emerald-600 hover:bg-emerald-50 px-4 py-2 rounded-xl transition-colors disabled:opacity-50"
                >
                  <Bot className="w-5 h-5" />
                  <span className="text-sm font-bold">Análise IA</span>
                </button>
                <span className={`px-3 py-1 rounded-full text-xs font-bold border ${provider.status === 'ACTIVE' ? 'bg-green-50 text-green-700 border-green-200' : 'bg-gray-50 text-gray-500 border-gray-200'}`}>
                  {provider.status}
                </span>
              </div>
            </div>

            <div className="p-6 bg-slate-50/30">
              <div className="flex items-center justify-between mb-4">
                <h4 className="text-sm font-bold text-slate-500 uppercase tracking-wider">Equipe Alocada</h4>
                <button className="text-xs font-bold text-emerald-600 flex items-center gap-1 hover:underline">
                  <UserPlus className="w-4 h-4" /> Cadastrar Funcionário
                </button>
              </div>

              <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
                {provider.employees.map(employee => (
                  <div 
                    key={employee.id} 
                    onClick={() => onViewEmployee(employee)}
                    className="bg-white p-4 rounded-xl border border-slate-200 hover:border-emerald-300 hover:shadow-md transition-all cursor-pointer group"
                  >
                    <div className="flex items-start justify-between mb-3">
                      <div>
                        <p className="font-bold text-slate-800">{employee.name}</p>
                        <p className="text-xs text-slate-500">{employee.role}</p>
                      </div>
                      <ChevronRight className="w-5 h-5 text-slate-300 group-hover:text-emerald-500 transition-colors" />
                    </div>
                    <div className="flex flex-wrap gap-2">
                      {employee.documents.map(doc => (
                        <span 
                          key={doc.id} 
                          title={`${doc.type} expira em ${doc.expiryDate}`}
                          className={`text-[10px] px-2 py-0.5 rounded-full border font-bold ${STATUS_COLORS[doc.status]}`}
                        >
                          {doc.type}
                        </span>
                      ))}
                    </div>
                  </div>
                ))}
              </div>
            </div>

            {aiAnalysis && (
              <div className="m-6 p-4 bg-emerald-50 border border-emerald-100 rounded-xl relative">
                <button onClick={() => setAiAnalysis(null)} className="absolute top-2 right-2 text-emerald-400 hover:text-emerald-600">×</button>
                <div className="flex gap-3">
                  <Bot className="w-6 h-6 text-emerald-600 shrink-0" />
                  <div>
                    <h5 className="text-sm font-bold text-emerald-800 mb-1">BioSafety AI Insight</h5>
                    <p className="text-sm text-emerald-700 whitespace-pre-wrap">{aiAnalysis}</p>
                  </div>
                </div>
              </div>
            )}
          </div>
        ))}
      </div>
    </div>
  );
};

export default ProviderList;

import React, { useState } from 'react';
import Layout from './components/Layout';
import Dashboard from './components/Dashboard';
import ProviderList from './components/ProviderList';
import EmployeeModal from './components/EmployeeModal';
import { MOCK_PROVIDERS } from './constants.tsx';
import { Provider, Employee } from './types';

const App: React.FC = () => {
  const [activeTab, setActiveTab] = useState('dashboard');
  const [providers, setProviders] = useState<Provider[]>(MOCK_PROVIDERS);
  const [selectedEmployee, setSelectedEmployee] = useState<Employee | null>(null);

  const handleAddProvider = () => {
    alert("Funcionalidade de cadastro em desenvolvimento para a planta de Biometano.");
  };

  const renderContent = () => {
    switch (activeTab) {
      case 'dashboard':
        return <Dashboard providers={providers} />;
      case 'providers':
        return (
          <ProviderList 
            providers={providers} 
            onAddProvider={handleAddProvider}
            onViewEmployee={setSelectedEmployee}
          />
        );
      case 'alerts':
        return (
          <div className="bg-white p-8 rounded-2xl border border-slate-200 text-center space-y-4">
            <div className="bg-red-50 w-16 h-16 rounded-full flex items-center justify-center mx-auto text-red-600">
              <span className="text-2xl font-bold">!</span>
            </div>
            <h2 className="text-xl font-bold">Alertas de Vencimento Imediato</h2>
            <p className="text-slate-500 max-w-md mx-auto">
              Nesta seção você visualizará apenas documentos expirados ou que vencem nos próximos 7 dias para ação imediata.
            </p>
            <div className="pt-6 grid gap-4 max-w-2xl mx-auto">
              {providers.flatMap(p => p.employees).flatMap(e => e.documents.filter(d => d.status === 'EXPIRED')).map((d, i) => (
                <div key={i} className="flex items-center justify-between p-4 bg-red-50 border border-red-100 rounded-xl text-left">
                  <div>
                    <p className="font-bold text-red-800">{d.type} Vencido</p>
                    <p className="text-xs text-red-700">Responsável: {providers.find(p => p.employees.some(emp => emp.documents.includes(d)))?.name}</p>
                  </div>
                  <button className="bg-red-600 text-white px-4 py-1.5 rounded-lg text-xs font-bold hover:bg-red-700 transition-colors">
                    Notificar Empresa
                  </button>
                </div>
              ))}
            </div>
          </div>
        );
      default:
        return (
          <div className="flex flex-col items-center justify-center min-h-[400px] text-slate-400">
            <p className="text-lg font-medium">Seção em construção</p>
          </div>
        );
    }
  };

  return (
    <Layout activeTab={activeTab} setActiveTab={setActiveTab}>
      {renderContent()}
      <EmployeeModal 
        employee={selectedEmployee} 
        onClose={() => setSelectedEmployee(null)} 
      />
    </Layout>
  );
};

export default App;

