import { createClient } from '@supabase/supabase-js';

const supabaseUrl = process.env.SUPABASE_URL || '';
const supabaseAnonKey = process.env.SUPABASE_ANON_KEY || '';

export const supabase = createClient(supabaseUrl, supabaseAnonKey);

import { supabase } from '../lib/supabase';
import { Provider, Employee, Document, DocStatus, ProviderStatus } from '../types';

export const dbService = {
  async getFullProvidersData(): Promise<Provider[]> {
    const { data, error } = await supabase
      .from('providers')
      .select(`
        *,
        employees (
          *,
          documents (*)
        )
      `)
      .order('name');

    if (error) {
      console.error('Erro ao buscar dados do Supabase:', error);
      throw error;
    }

    // Mapeamento simples para garantir compatibilidade com os tipos do frontend
    return data.map((p: any) => ({
      ...p,
      status: p.status as ProviderStatus,
      employees: p.employees.map((e: any) => ({
        ...e,
        documents: e.documents.map((d: any) => ({
          ...d,
          status: d.status as DocStatus,
          issueDate: d.issue_date,
          expiryDate: d.expiry_date
        }))
      }))
    })) as Provider[];
  },

  async createProvider(provider: Partial<Provider>) {
    const { data, error } = await supabase
      .from('providers')
      .insert([provider])
      .select();

    if (error) throw error;
    return data[0];
  },

  async createEmployee(employee: Partial<Employee>) {
    const { data, error } = await supabase
      .from('employees')
      .insert([employee])
      .select();

    if (error) throw error;
    return data[0];
  }
};

import React, { useState, useEffect } from 'react';
import Layout from './components/Layout';
import Dashboard from './components/Dashboard';
import ProviderList from './components/ProviderList';
import EmployeeModal from './components/EmployeeModal';
import AddProviderModal from './components/AddProviderModal';
import { dbService } from './services/dbService';
import { Provider, Employee } from './types';
import { Loader2, RefreshCcw, DatabaseZap } from 'lucide-react';

const App: React.FC = () => {
  const [activeTab, setActiveTab] = useState('dashboard');
  const [providers, setProviders] = useState<Provider[]>([]);
  const [selectedEmployee, setSelectedEmployee] = useState<Employee | null>(null);
  const [isAddProviderOpen, setIsAddProviderOpen] = useState(false);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);

  const fetchData = async () => {
    try {
      setLoading(true);
      setError(null);
      const data = await dbService.getFullProvidersData();
      setProviders(data);
    } catch (err: any) {
      setError("Falha ao conectar com o Supabase. Verifique se as variáveis SUPABASE_URL e SUPABASE_ANON_KEY estão configuradas.");
      console.error(err);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchData();
  }, []);

  const handleProviderAdded = () => {
    setIsAddProviderOpen(false);
    fetchData(); // Atualiza a lista após cadastro
  };

  const renderContent = () => {
    if (loading) {
      return (
        <div className="flex flex-col items-center justify-center min-h-[400px] text-emerald-500">
          <Loader2 className="w-12 h-12 animate-spin mb-4" />
          <p className="text-slate-400 font-medium animate-pulse">Sincronizando com Biometano Caieiras...</p>
        </div>
      );
    }

    if (error) {
      return (
        <div className="flex flex-col items-center justify-center min-h-[400px] text-center p-8 bg-slate-900 rounded-3xl border border-red-900/20">
          <DatabaseZap className="w-16 h-16 text-red-500 mb-4 opacity-50" />
          <h2 className="text-xl font-bold text-white mb-2">Erro de Conexão</h2>
          <p className="text-slate-400 max-w-md mb-6">{error}</p>
          <button 
            onClick={fetchData}
            className="flex items-center gap-2 bg-slate-800 hover:bg-slate-700 text-white px-6 py-2 rounded-xl transition-all"
          >
            <RefreshCcw className="w-4 h-4" />
            Tentar Novamente
          </button>
        </div>
      );
    }

    switch (activeTab) {
      case 'dashboard':
        return <Dashboard providers={providers} />;
      case 'providers':
        return (
          <ProviderList 
            providers={providers} 
            onAddProvider={() => setIsAddProviderOpen(true)}
            onViewEmployee={setSelectedEmployee}
          />
        );
      case 'alerts':
        const expiredDocs = providers.flatMap(p => p.employees).flatMap(e => e.documents.filter(d => d.status === 'EXPIRED'));
        return (
          <div className="bg-slate-900 p-8 rounded-2xl border border-slate-800 text-center space-y-6">
            <div className="bg-red-500/10 w-20 h-20 rounded-full flex items-center justify-center mx-auto text-red-500 border border-red-500/20">
              <span className="text-3xl font-black">!</span>
            </div>
            <div>
              <h2 className="text-2xl font-black text-white">Alertas de Segurança</h2>
              <p className="text-slate-500 mt-2">Documentos expirados que impedem o trabalho em campo.</p>
            </div>

            <div className="pt-6 grid gap-4 max-w-3xl mx-auto">
              {expiredDocs.length > 0 ? (
                expiredDocs.map((d, i) => (
                  <div key={i} className="flex items-center justify-between p-5 bg-slate-950 border border-red-900/20 rounded-2xl text-left group hover:border-red-500/40 transition-all">
                    <div>
                      <p className="font-bold text-red-400 uppercase text-xs tracking-widest">{d.type} EXPIRADO</p>
                      <p className="text-slate-200 font-bold text-lg mt-1">Vencimento: {new Date(d.expiryDate).toLocaleDateString('pt-BR')}</p>
                    </div>
                    <button className="bg-red-600/10 hover:bg-red-600 text-red-500 hover:text-white px-6 py-2 rounded-xl text-xs font-black uppercase tracking-widest transition-all border border-red-500/20">
                      Notificar Empresa
                    </button>
                  </div>
                ))
              ) : (
                <div className="text-emerald-500 bg-emerald-500/5 p-8 rounded-2xl border border-emerald-500/20">
                  Nenhum documento crítico vencido hoje.
                </div>
              )}
            </div>
          </div>
        );
      default:
        return null;
    }
  };

  return (
    <Layout activeTab={activeTab} setActiveTab={setActiveTab}>
      {renderContent()}

      <EmployeeModal 
        employee={selectedEmployee} 
        onClose={() => setSelectedEmployee(null)} 
      />

      <AddProviderModal 
        isOpen={isAddProviderOpen}
        onClose={() => setIsAddProviderOpen(false)}
        onSuccess={handleProviderAdded}
      />
    </Layout>
  );
};

export default App;

