  const [isAdding, setIsAdding] = useState(false);
  const [newUserData, setNewUserData] = useState({ name: '', email: '' });

  const fetchUsers = async () => {
    try {
      setLoading(true);
      const { data, error } = await (window as any).supabase.from('app_users').select('*');
      if (!error) setUsers(data || []);
    } catch (err) {
      console.error(err);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchUsers();
  }, []);

  const handleAddUser = async (e: React.FormEvent) => {
    e.preventDefault();
    try {
      const { error } = await (window as any).supabase.from('app_users').insert([{
        ...newUserData,
        role: 'OPERATOR'
      }]);
      if (error) throw error;
      setIsAdding(false);
      setNewUserData({ name: '', email: '' });
