      <AddDocumentModal 
        isOpen={isAddingDoc}
        employeeId={employee.id}
        employeeName={employee.name}
        onClose={() => setIsAddingDoc(false)}
        onSuccess={() => {
          setIsAddingDoc(false);
          onRefresh();
        }}
      />
    </>
  );
};

