    return (data || []).map((p: any) => ({
      ...p,
      status: p.status as ProviderStatus,
      contactEmail: p.contact_email,
      employees: (p.employees || []).map((e: any) => ({
        ...e,
        documents: (e.documents || []).map((d: any) => {
          const expiryDateStr = d.expiry_date || d.expiryDate;
          const expiryDate = expiryDateStr ? new Date(expiryDateStr) : new Date();
          const today = new Date();
          const diffDays = Math.ceil((expiryDate.getTime() - today.getTime()) / (1000 * 60 * 60 * 24));

          let status: DocStatus = DocStatus.VALID;
          if (diffDays < 0) status = DocStatus.EXPIRED;
          else if (diffDays <= 30) status = DocStatus.EXPIRING;

          return {
            id: d.id,
            type: d.type,
            issueDate: d.issue_date || d.issueDate,
            expiryDate: expiryDateStr,
            status,
            fileUrl: d.file_url || d.fileUrl,
            description: d.description
          } as Document;
        })
      }))
    })) as Provider[];
  },

  async createProvider(provider: { name: string, cnpj: string, contact_email: string }) {
    const { data, error } = await supabase
      .from('providers')
      .insert([provider])
      .select();
    if (error) throw error;
    return data[0];
  },

  async updateProvider(id: string, updates: { name: string, cnpj: string, contact_email: string }) {
    const { data, error } = await supabase
      .from('providers')
      .update(updates)
      .eq('id', id)
      .select();
    if (error) throw error;
    return data[0];
  },

  async deleteProvider(id: string) {
    const { error } = await supabase
      .from('providers')
      .delete()
      .eq('id', id);
    if (error) throw error;
  },

  async createEmployee(employee: { name: string, cpf: string, role: string, provider_id: string }) {
    const { data, error } = await supabase
      .from('employees')
      .insert([employee])
      .select();
    if (error) throw error;
    return data[0];
  },

  async createDocument(document: { employee_id: string, type: string, issue_date: string, expiry_date: string }) {
    const { data, error } = await supabase
      .from('documents')
      .insert([document])
      .select();
    if (error) throw error;
    return data[0];
  },

  async deleteDocument(id: string) {
    const { error } = await supabase.from('documents').delete().eq('id', id);
    if (error) throw error;
  },

  async getTrainingTypes(): Promise<string[]> {
    try {
      const { data, error } = await supabase.from('training_types').select('name').order('name');
      if (error || !data) throw error;
      return data.map((t: any) => t.name);
    } catch {
      return ['NR-01', 'NR-06', 'NR-10', 'NR-20', 'NR-33', 'NR-35', 'ASO'];
    }
  },

  async addTrainingType(name: string) {
    const { error } = await supabase.from('training_types').insert([{ name }]);
    if (error) throw error;
  },

  async deleteTrainingType(name: string) {
    const { error } = await supabase.from('training_types').delete().eq('name', name);
    if (error) throw error;
  }
};

import React, { useState } from 'react';
import { Plus, Search, ChevronRight, UserPlus, Bot, ShieldAlert, Edit2, Trash2, Loader2 } from 'lucide-react';
import { Provider, Employee } from '../types';
import { analyzeCompliance } from '../services/geminiService';
import { dbService } from '../services/dbService';
import AddEmployeeModal from './AddEmployeeModal';

interface ProviderListProps {
  providers: Provider[];
  onAddProvider: () => void;
  onEditProvider: (provider: Provider) => void;
  onViewEmployee: (emp: Employee) => void;
  onRefresh: () => void;
}

const ProviderList: React.FC<ProviderListProps> = ({ providers, onAddProvider, onEditProvider, onViewEmployee, onRefresh }) => {
  const [searchTerm, setSearchTerm] = useState('');
  const [aiAnalysis, setAiAnalysis] = useState<Record<string, string>>({});
  const [analyzingIds, setAnalyzingIds] = useState<Set<string>>(new Set());
  const [deletingId, setDeletingId] = useState<string | null>(null);
  const [activeAddEmployee, setActiveAddEmployee] = useState<{id: string, name: string} | null>(null);

  const filteredProviders = providers.filter(p => 
    p.name?.toLowerCase().includes(searchTerm.toLowerCase()) || 
    p.cnpj?.includes(searchTerm)
  );

  const handleAiAnalysis = async (provider: Provider) => {
    setAnalyzingIds(prev => new Set(prev).add(provider.id));
    const dataStr = provider.employees?.map(e => `${e.name} (${e.role}): ${e.documents?.map(d => `${d.type}-${d.status}`).join(', ')}`).join('; ') || 'Nenhum funcionário';
    const result = await analyzeCompliance(provider.name, dataStr);
    if (result) {
      setAiAnalysis(prev => ({ ...prev, [provider.id]: result }));
    }
    setAnalyzingIds(prev => {
      const next = new Set(prev);
      next.delete(provider.id);
      return next;
    });
  };

  const handleDeleteProvider = async (id: string, name: string) => {
    if (!confirm(`Deseja remover permanentemente o prestador ${name} e todos os seus colaboradores?`)) return;
    setDeletingId(id);
    try {
      await dbService.deleteProvider(id);
      onRefresh();
    } catch (err) {
      alert("Erro ao excluir prestador.");
    } finally {
      setDeletingId(null);
    }
  };

  return (
    <div className="space-y-6">
      <div className="flex flex-col md:flex-row gap-4 items-center justify-between bg-slate-900 p-4 rounded-2xl border border-slate-800 shadow-xl">
        <div className="relative w-full md:w-96">
          <Search className="absolute left-3 top-1/2 -translate-y-1/2 w-4 h-4 text-slate-400" />
          <input 
            type="text"
            placeholder="Buscar fornecedor ou CNPJ..."
            className="w-full pl-10 pr-4 py-2 bg-slate-950 border border-slate-800 rounded-xl focus:ring-2 focus:ring-emerald-600 focus:outline-none transition-all text-sm text-slate-200"
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
          />
        </div>
        <button 
          onClick={onAddProvider}
          className="w-full md:w-auto flex items-center justify-center gap-2 bg-emerald-600 hover:bg-emerald-700 text-white px-6 py-2.5 rounded-xl font-bold text-sm transition-all shadow-lg shadow-emerald-900/20 active:scale-95"
        >
          <Plus className="w-4 h-4" />
          Cadastrar Prestadora
        </button>
      </div>

      <div className="grid grid-cols-1 gap-6">
        {filteredProviders.length > 0 ? filteredProviders.map(provider => (
          <div key={provider.id} className="bg-slate-900 rounded-2xl border border-slate-800 shadow-xl overflow-hidden group hover:border-slate-700 transition-all">
            <div className="p-6 border-b border-slate-800 flex items-center justify-between">
              <div className="flex items-center gap-4">
                <div className="w-12 h-12 bg-slate-800 rounded-xl flex items-center justify-center font-black text-emerald-500 border border-slate-700">
                  {provider.name?.charAt(0)}
                </div>
                <div>
                  <h3 className="text-lg font-bold text-white">{provider.name}</h3>
                  <div className="flex items-center gap-3 mt-1">
                    <span className="text-xs text-slate-500 font-bold uppercase tracking-tight">{provider.cnpj}</span>
                    <span className="w-1 h-1 bg-slate-700 rounded-full"></span>
                    <span className="text-xs text-slate-400 font-medium">{provider.contactEmail}</span>
                  </div>
                </div>
              </div>
              <div className="flex items-center gap-2">
                <button 
                  onClick={() => handleAiAnalysis(provider)}
                  disabled={analyzingIds.has(provider.id)}
                  className={`flex items-center gap-2 px-4 py-2 rounded-xl transition-all border ${analyzingIds.has(provider.id) ? 'bg-slate-800 text-slate-600 border-slate-700' : 'bg-emerald-500/10 text-emerald-400 border-emerald-500/20 hover:bg-emerald-500 hover:text-white'}`}
                >
                  <Bot className={`w-4 h-4 ${analyzingIds.has(provider.id) ? 'animate-bounce' : ''}`} />
                  <span className="text-[10px] font-black uppercase tracking-widest">Auditoria IA</span>
                </button>
                <button 
                  onClick={() => onEditProvider(provider)}
                  className="p-2 text-slate-400 hover:text-white hover:bg-slate-800 rounded-lg transition-all border border-transparent hover:border-slate-700"
                >
                  <Edit2 className="w-4 h-4" />
                </button>
                <button 
                  disabled={deletingId === provider.id}
                  onClick={() => handleDeleteProvider(provider.id, provider.name)}
                  className="p-2 text-slate-400 hover:text-red-500 hover:bg-red-500/10 rounded-lg transition-all"
                >
                  {deletingId === provider.id ? <Loader2 className="w-4 h-4 animate-spin" /> : <Trash2 className="w-4 h-4" />}
                </button>
              </div>
            </div>

            <div className="p-6 bg-slate-950/40">
              <div className="flex items-center justify-between mb-4">
                <h4 className="text-[10px] font-black text-slate-500 uppercase tracking-[0.2em]">Quadro de Colaboradores</h4>
                <button 
                  onClick={() => setActiveAddEmployee({ id: provider.id, name: provider.name })}
                  className="text-xs font-bold text-emerald-500 flex items-center gap-1 hover:text-emerald-400"
                >
                  <UserPlus className="w-3 h-3" /> Adicionar Colaborador
                </button>
              </div>

              <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
                {provider.employees?.map(employee => (
                  <div 
                    key={employee.id} 
                    onClick={() => onViewEmployee(employee)}
                    className="bg-slate-900 p-4 rounded-xl border border-slate-800 hover:border-emerald-500/50 hover:shadow-2xl transition-all cursor-pointer group"
                  >
                    <div className="flex items-start justify-between mb-3">
                      <div>
                        <p className="font-bold text-white group-hover:text-emerald-400 transition-colors">{employee.name}</p>
                        <p className="text-[10px] text-slate-500 font-bold uppercase tracking-tight">{employee.role}</p>
                      </div>
                      <ChevronRight className="w-4 h-4 text-slate-600 group-hover:text-emerald-500 transition-colors" />
                    </div>
                    <div className="flex flex-wrap gap-1.5">
                      {employee.documents?.map(doc => (
                        <span 
                          key={doc.id} 
                          className={`text-[9px] px-2 py-0.5 rounded-md border font-black uppercase tracking-tighter ${
                            doc.status === 'EXPIRED' ? 'bg-red-500/10 text-red-500 border-red-500/20' : 
                            doc.status === 'EXPIRING' ? 'bg-amber-500/10 text-amber-500 border-amber-500/20' : 
                            'bg-emerald-500/10 text-emerald-500 border-emerald-500/20'
                          }`}
                        >
                          {doc.type}
                        </span>
                      ))}
                    </div>
                  </div>
                ))}
                {(!provider.employees || provider.employees.length === 0) && (
                  <p className="text-xs text-slate-600 italic py-4">Nenhum funcionário cadastrado ainda.</p>
                )}
              </div>
            </div>

            {aiAnalysis[provider.id] && (
              <div className="m-6 p-5 bg-emerald-500/5 border border-emerald-500/20 rounded-2xl relative animate-in fade-in zoom-in-95 duration-300">
                <button onClick={() => setAiAnalysis(prev => {const n={...prev}; delete n[provider.id]; return n;})} className="absolute top-4 right-4 text-slate-500 hover:text-white transition-colors">
                  <Plus className="w-5 h-5 rotate-45" />
                </button>
                <div className="flex gap-4">
                  <div className="bg-emerald-500/20 p-2 rounded-lg h-fit">
                    <ShieldAlert className="w-6 h-6 text-emerald-400" />
                  </div>
                  <div>
                    <h5 className="text-xs font-black text-emerald-400 mb-2 uppercase tracking-widest">Parecer Gemini AI - Caieiras</h5>
                    <p className="text-sm text-slate-300 leading-relaxed italic">"{aiAnalysis[provider.id]}"</p>
                  </div>
                </div>
              </div>
            )}
          </div>
        )) : (
          <div className="text-center py-20 bg-slate-900 rounded-3xl border border-dashed border-slate-800">
            <p className="text-slate-500 font-medium">Nenhum fornecedor cadastrado na base de dados.</p>
            <button onClick={onAddProvider} className="text-emerald-500 font-bold mt-2 hover:underline">Cadastrar o primeiro agora</button>
          </div>
        )}
      </div>

      <AddEmployeeModal 
        isOpen={!!activeAddEmployee}
        providerId={activeAddEmployee?.id || ''}
        providerName={activeAddEmployee?.name || ''}
        onClose={() => setActiveAddEmployee(null)}
        onSuccess={() => {
          setActiveAddEmployee(null);
          onRefresh();
        }}
      />
    </div>
  );
};

export default ProviderList;

import React, { useState, useEffect } from 'react';
import { X, Factory, Send, Loader2, Save } from 'lucide-react';
import { dbService } from '../services/dbService';
import { Provider } from '../types';

interface AddProviderModalProps {
  isOpen: boolean;
  providerToEdit?: Provider | null;
  onClose: () => void;
  onSuccess: () => void;
}

const AddProviderModal: React.FC<AddProviderModalProps> = ({ isOpen, providerToEdit, onClose, onSuccess }) => {
  const [loading, setLoading] = useState(false);
  const [formData, setFormData] = useState({
    name: '',
    cnpj: '',
    contact_email: '',
  });

  useEffect(() => {
    if (providerToEdit) {
      setFormData({
        name: providerToEdit.name,
        cnpj: providerToEdit.cnpj,
        contact_email: providerToEdit.contactEmail,
      });
    } else {
      setFormData({ name: '', cnpj: '', contact_email: '' });
    }
  }, [providerToEdit, isOpen]);

  if (!isOpen) return null;

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    setLoading(true);
    try {
      if (providerToEdit) {
        await dbService.updateProvider(providerToEdit.id, formData);
      } else {
        await dbService.createProvider(formData);
      }
      onSuccess();
    } catch (err: any) {
      alert(`Erro: ${err.message || "Verifique os dados ou as permissões do banco."}`);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="fixed inset-0 z-50 flex items-center justify-center p-4 bg-black/80 backdrop-blur-md">
      <div className="bg-slate-900 w-full max-w-lg rounded-[2.5rem] shadow-2xl border border-slate-800 overflow-hidden animate-in fade-in zoom-in-95 duration-200">
        <div className="p-8 border-b border-slate-800 flex items-center justify-between">
          <div className="flex items-center gap-4">
            <div className="bg-emerald-600/20 p-3 rounded-2xl border border-emerald-500/20">
              <Factory className="w-6 h-6 text-emerald-400" />
            </div>
            <div>
              <h2 className="text-xl font-black text-white">{providerToEdit ? 'Editar Prestador' : 'Novo Prestador'}</h2>
              <p className="text-xs text-slate-500 font-bold uppercase tracking-wider">Protocolo de Cadastro Empresarial</p>
            </div>
          </div>
          <button onClick={onClose} className="p-2 hover:bg-slate-800 rounded-full transition-colors">
            <X className="w-6 h-6 text-slate-400" />
          </button>
        </div>

        <form onSubmit={handleSubmit} className="p-8 space-y-6">
          <div className="space-y-2">
            <label className="text-[10px] font-black text-slate-500 uppercase tracking-[0.2em] ml-1 text-white">Razão Social / Nome Fantasia</label>
            <input 
              required
              className="w-full bg-slate-950 border border-slate-800 rounded-2xl px-5 py-3.5 text-white focus:ring-2 focus:ring-emerald-600 focus:outline-none transition-all placeholder:text-slate-700"
              placeholder="Ex: Purifica Gás Ltda"
              value={formData.name}
              onChange={(e) => setFormData({...formData, name: e.target.value})}
            />
          </div>

          <div className="space-y-2">
            <label className="text-[10px] font-black text-slate-500 uppercase tracking-[0.2em] ml-1 text-white">CNPJ</label>
            <input 
              required
              className="w-full bg-slate-950 border border-slate-800 rounded-2xl px-5 py-3.5 text-white focus:ring-2 focus:ring-emerald-600 focus:outline-none transition-all placeholder:text-slate-700"
              placeholder="00.000.000/0000-00"
              value={formData.cnpj}
              onChange={(e) => setFormData({...formData, cnpj: e.target.value})}
            />
          </div>

          <div className="space-y-2">
            <label className="text-[10px] font-black text-slate-500 uppercase tracking-[0.2em] ml-1 text-white">E-mail de Contato</label>
            <input 
              required
              type="email"
              className="w-full bg-slate-950 border border-slate-800 rounded-2xl px-5 py-3.5 text-white focus:ring-2 focus:ring-emerald-600 focus:outline-none transition-all placeholder:text-slate-700"
              placeholder="rh@empresa.com.br"
              value={formData.contact_email}
              onChange={(e) => setFormData({...formData, contact_email: e.target.value})}
            />
          </div>

          <div className="pt-4 flex gap-4">
            <button 
              type="button"
              onClick={onClose}
              className="flex-1 px-6 py-4 bg-slate-800 text-white rounded-2xl font-black text-xs uppercase tracking-widest hover:bg-slate-700 transition-all border border-slate-700"
            >
              Cancelar
            </button>
            <button 
              type="submit"
              disabled={loading}
              className="flex-1 flex items-center justify-center gap-3 px-6 py-4 bg-emerald-600 hover:bg-emerald-500 disabled:bg-slate-800 text-white rounded-2xl font-black text-xs uppercase tracking-widest transition-all shadow-xl shadow-emerald-900/20 active:scale-95"
            >
              {loading ? (
                <Loader2 className="w-4 h-4 animate-spin" />
              ) : (
                <>
                  <Save className="w-4 h-4" />
                  {providerToEdit ? 'Salvar Alteração' : 'Cadastrar'}
                </>
              )}
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

