                  )) : (
                    <tr>
                      <td colSpan={5} className="py-10 text-center text-slate-500 text-sm italic">Nenhum certificado lançado para este colaborador.</td>
                    </tr>
                  )}
                </tbody>
              </table>
            </div>
          </div>

          <div className="p-8 bg-slate-800/50 border-t border-slate-800 flex justify-end gap-4">
             <button onClick={onClose} className="px-8 py-3 bg-slate-900 border border-slate-700 text-slate-300 rounded-xl font-bold text-xs uppercase tracking-widest hover:text-white hover:border-slate-500 transition-all">
               Fechar
             </button>
          </div>
        </div>
      </div>

      <AddDocumentModal 
        isOpen={isAddingDoc}
        employeeId={employee.id}
        employeeName={employee.name}
        onClose={() => setIsAddingDoc(false)}
        onSuccess={() => {
          setIsAddingDoc(false);
          onRefresh();
        }}
      />
    </>
  );
};

export default EmployeeModal;

import React, { useState, useEffect } from 'react';
import Layout from './components/Layout';
import Dashboard from './components/Dashboard';
import ProviderList from './components/ProviderList';
import EmployeeModal from './components/EmployeeModal';
import AddProviderModal from './components/AddProviderModal';
import UserAdmin from './components/UserAdmin';
import Settings from './components/Settings';
import Login from './components/Login';
import { dbService } from './services/dbService';
import { Provider, Employee } from './types';
import { Loader2, RefreshCcw, DatabaseZap } from 'lucide-react';

const App: React.FC = () => {
  const [isLoggedIn, setIsLoggedIn] = useState(() => localStorage.getItem('bio_auth') === 'true');
  const [userEmail, setUserEmail] = useState(() => localStorage.getItem('bio_user') || '');
  const [activeTab, setActiveTab] = useState('dashboard');
  const [providers, setProviders] = useState<Provider[]>([]);
  const [selectedEmployeeId, setSelectedEmployeeId] = useState<string | null>(null);
  const [isAddProviderOpen, setIsAddProviderOpen] = useState(false);
  const [providerToEdit, setProviderToEdit] = useState<Provider | null>(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);

  const fetchData = async () => {
    if (!isLoggedIn) return;
    try {
      setLoading(true);
      setError(null);
      const data = await dbService.getFullProvidersData();
      setProviders(data);
    } catch (err: any) {
      setError("Falha ao conectar com o Supabase. Verifique as tabelas ou a rede.");
      console.error(err);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchData();
  }, [isLoggedIn]);

  const handleLogin = (email: string) => {
    setIsLoggedIn(true);
    setUserEmail(email);
    localStorage.setItem('bio_auth', 'true');
    localStorage.setItem('bio_user', email);
  };

  const handleLogout = () => {
    setIsLoggedIn(false);
    setUserEmail('');
    localStorage.removeItem('bio_auth');
    localStorage.removeItem('bio_user');
  };

  const handleEditProvider = (provider: Provider) => {
    setProviderToEdit(provider);
    setIsAddProviderOpen(true);
  };

  const handleCloseProviderModal = () => {
    setIsAddProviderOpen(false);
    setProviderToEdit(null);
  };

  const selectedEmployee = React.useMemo(() => {
    if (!selectedEmployeeId) return null;
    for (const p of providers) {
      const emp = p.employees?.find(e => e.id === selectedEmployeeId);
      if (emp) return emp;
    }
    return null;
  }, [selectedEmployeeId, providers]);

  const handleRefresh = () => fetchData();

  if (!isLoggedIn) {
    return <Login onLogin={handleLogin} />;
  }

  const renderContent = () => {
    if (loading && activeTab !== 'settings') {
      return (
        <div className="flex flex-col items-center justify-center min-h-[400px] text-emerald-500">
          <Loader2 className="w-12 h-12 animate-spin mb-4" />
          <p className="text-slate-400 font-bold uppercase tracking-widest text-xs animate-pulse">Sincronizando Dados...</p>
        </div>
      );
    }

    if (error) {
      return (
        <div className="flex flex-col items-center justify-center min-h-[400px] text-center p-12 bg-slate-900 rounded-[2.5rem] border border-red-900/20">
          <DatabaseZap className="w-16 h-16 text-red-500 mb-4 opacity-50" />
          <h2 className="text-xl font-black text-white mb-2 uppercase tracking-tight">Erro de Conexão</h2>
          <p className="text-slate-400 max-w-md mb-6 text-sm">{error}</p>
          <button onClick={fetchData} className="flex items-center gap-2 bg-slate-800 hover:bg-slate-700 text-white px-8 py-3 rounded-xl transition-all font-black text-xs uppercase tracking-widest">
            <RefreshCcw className="w-4 h-4" /> Tentar Novamente
          </button>
        </div>
      );
    }

    switch (activeTab) {
      case 'dashboard':
        return <Dashboard providers={providers} />;
      case 'providers':
        return (
          <ProviderList 
            providers={providers} 
            onAddProvider={() => setIsAddProviderOpen(true)}
            onEditProvider={handleEditProvider}
            onViewEmployee={(emp) => setSelectedEmployeeId(emp.id)}
            onRefresh={handleRefresh}
          />
        );
      case 'users':
        return <UserAdmin />;
      case 'settings':
        return <Settings />;
      case 'alerts':
        const expiredDocs = providers.flatMap(p => p.employees || []).flatMap(e => (e.documents || []).filter(d => d.status === 'EXPIRED'));
        return (
          <div className="bg-slate-900 p-12 rounded-[2.5rem] border border-slate-800 text-center space-y-6">
            <div className="bg-red-500/10 w-24 h-24 rounded-full flex items-center justify-center mx-auto text-red-500 border border-red-500/20">
              <span className="text-4xl font-black text-white">!</span>
            </div>
            <div>
              <h2 className="text-3xl font-black text-white uppercase tracking-tight">Alertas de Segurança</h2>
              <p className="text-slate-500 mt-2 font-medium">Documentos expirados que exigem atenção imediata.</p>
            </div>

            <div className="pt-8 grid gap-4 max-w-3xl mx-auto">
              {expiredDocs.length > 0 ? (
                expiredDocs.map((d, i) => (
                  <div key={i} className="flex items-center justify-between p-6 bg-slate-950 border border-red-900/20 rounded-2xl text-left group hover:border-red-500/40 transition-all">
                    <div>
                      <p className="font-black text-red-400 uppercase text-[10px] tracking-widest">{d.type} EXPIRADO</p>
                      <p className="text-slate-200 font-bold text-xl mt-1">Vencimento: {new Date(d.expiryDate).toLocaleDateString('pt-BR')}</p>
                    </div>
                    <button className="bg-red-600 text-white px-6 py-2.5 rounded-xl text-[10px] font-black uppercase tracking-widest transition-all shadow-lg shadow-red-900/20">
                      Notificar Empresa
                    </button>
                  </div>
                ))
              ) : (
                <div className="text-emerald-500 bg-emerald-500/5 p-12 rounded-[2rem] border border-emerald-500/20 font-black uppercase tracking-widest text-sm">
                  Protocolo em conformidade. Nenhum vencimento crítico.
                </div>
              )}
            </div>
          </div>
        );
      default:
        return null;
    }
  };

  return (
    <Layout activeTab={activeTab} setActiveTab={setActiveTab} onLogout={handleLogout} userEmail={userEmail}>
      {renderContent()}

      {selectedEmployeeId && (
        <EmployeeModal 
          employee={selectedEmployee} 
          onClose={() => setSelectedEmployeeId(null)} 
          onRefresh={handleRefresh}
        />
      )}

      <AddProviderModal 
        isOpen={isAddProviderOpen}
        providerToEdit={providerToEdit}
        onClose={handleCloseProviderModal}
        onSuccess={() => {
          handleCloseProviderModal();
          handleRefresh();
        }}
      />
    </Layout>
  );
};

export default App;

import React, { useState } from 'react';
import { Plus, Search, ChevronRight, UserPlus, Bot, ShieldAlert, Edit2, Trash2, Loader2 } from 'lucide-react';
import { Provider, Employee } from '../types';
import { analyzeCompliance } from '../services/geminiService';
import { dbService } from '../services/dbService';
import AddEmployeeModal from './AddEmployeeModal';

interface ProviderListProps {
  providers: Provider[];
  onAddProvider: () => void;
  onEditProvider: (provider: Provider) => void;
  onViewEmployee: (emp: Employee) => void;
  onRefresh: () => void;
}

const ProviderList: React.FC<ProviderListProps> = ({ providers, onAddProvider, onEditProvider, onViewEmployee, onRefresh }) => {
  const [searchTerm, setSearchTerm] = useState('');
  const [aiAnalysis, setAiAnalysis] = useState<Record<string, string>>({});
  const [analyzingIds, setAnalyzingIds] = useState<Set<string>>(new Set());
  const [deletingId, setDeletingId] = useState<string | null>(null);
  const [activeAddEmployee, setActiveAddEmployee] = useState<{id: string, name: string} | null>(null);

  const filteredProviders = providers.filter(p => 
    p.name?.toLowerCase().includes(searchTerm.toLowerCase()) || 
    p.cnpj?.includes(searchTerm)
  );

  const handleAiAnalysis = async (provider: Provider) => {
    setAnalyzingIds(prev => new Set(prev).add(provider.id));
    const dataStr = provider.employees?.map(e => `${e.name} (${e.role}): ${e.documents?.map(d => `${d.type}-${d.status}`).join(', ')}`).join('; ') || 'Nenhum funcionário';
    const result = await analyzeCompliance(provider.name, dataStr);
    if (result) {
      setAiAnalysis(prev => ({ ...prev, [provider.id]: result }));
    }
    setAnalyzingIds(prev => {
      const next = new Set(prev);
      next.delete(provider.id);
      return next;
    });
  };

  const handleDeleteProvider = async (id: string, name: string) => {
    if (!confirm(`Deseja remover permanentemente o prestador ${name} e todos os seus colaboradores?`)) return;
    setDeletingId(id);
    try {
      await dbService.deleteProvider(id);
      onRefresh();
    } catch (err) {
      alert("Erro ao excluir prestador.");
    } finally {
      setDeletingId(null);
    }
  };

  return (
    <div className="space-y-6">
      <div className="flex flex-col md:flex-row gap-4 items-center justify-between bg-slate-900 p-4 rounded-2xl border border-slate-800 shadow-xl">
        <div className="relative w-full md:w-96">
          <Search className="absolute left-3 top-1/2 -translate-y-1/2 w-4 h-4 text-slate-400" />
          <input 
            type="text"
            placeholder="Buscar fornecedor ou CNPJ..."
            className="w-full pl-10 pr-4 py-2 bg-slate-950 border border-slate-800 rounded-xl focus:ring-2 focus:ring-emerald-600 focus:outline-none transition-all text-sm text-slate-200"
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
          />
        </div>
        <button 
          onClick={onAddProvider}
          className="w-full md:w-auto flex items-center justify-center gap-2 bg-emerald-600 hover:bg-emerald-700 text-white px-6 py-2.5 rounded-xl font-bold text-sm transition-all shadow-lg shadow-emerald-900/20 active:scale-95"
        >
          <Plus className="w-4 h-4 text-white" />
          Cadastrar Prestadora
        </button>
      </div>

      <div className="grid grid-cols-1 gap-6">
        {filteredProviders.length > 0 ? filteredProviders.map(provider => (
          <div key={provider.id} className="bg-slate-900 rounded-2xl border border-slate-800 shadow-xl overflow-hidden group hover:border-slate-700 transition-all">
            <div className="p-6 border-b border-slate-800 flex items-center justify-between">
              <div className="flex items-center gap-4">
                <div className="w-12 h-12 bg-slate-800 rounded-xl flex items-center justify-center font-black text-emerald-500 border border-slate-700">
                  {provider.name?.charAt(0)}
                </div>
                <div>
                  <h3 className="text-lg font-bold text-white">{provider.name}</h3>
                  <div className="flex items-center gap-3 mt-1">
                    <span className="text-xs text-slate-500 font-bold uppercase tracking-tight">{provider.cnpj}</span>
                    <span className="w-1 h-1 bg-slate-700 rounded-full"></span>
                    <span className="text-xs text-slate-400 font-medium">{provider.contactEmail}</span>
                  </div>
                </div>
              </div>
              <div className="flex items-center gap-2">
                <button 
                  onClick={() => handleAiAnalysis(provider)}
                  disabled={analyzingIds.has(provider.id)}
                  className={`flex items-center gap-2 px-4 py-2 rounded-xl transition-all border ${analyzingIds.has(provider.id) ? 'bg-slate-800 text-slate-600 border-slate-700' : 'bg-emerald-500/10 text-emerald-400 border-emerald-500/20 hover:bg-emerald-500 hover:text-white'}`}
                >
                  <Bot className={`w-4 h-4 text-white ${analyzingIds.has(provider.id) ? 'animate-bounce' : ''}`} />
                  <span className="text-[10px] font-black uppercase tracking-widest">Auditoria IA</span>
                </button>
                <button 
                  onClick={() => onEditProvider(provider)}
                  className="p-2 text-slate-400 hover:text-white hover:bg-slate-800 rounded-lg transition-all border border-transparent hover:border-slate-700"
                  title="Editar Prestador"
                >
                  <Edit2 className="w-4 h-4 text-white" />
                </button>
                <button 
                  disabled={deletingId === provider.id}
                  onClick={() => handleDeleteProvider(provider.id, provider.name)}
                  className="p-2 text-slate-400 hover:text-red-500 hover:bg-red-500/10 rounded-lg transition-all"
                  title="Excluir Prestador"
                >
                  {deletingId === provider.id ? <Loader2 className="w-4 h-4 animate-spin text-white" /> : <Trash2 className="w-4 h-4 text-white" />}
                </button>
              </div>
            </div>

            <div className="p-6 bg-slate-950/40">
              <div className="flex items-center justify-between mb-4">
                <h4 className="text-[10px] font-black text-slate-500 uppercase tracking-[0.2em]">Quadro de Colaboradores</h4>
                <button 
                  onClick={() => setActiveAddEmployee({ id: provider.id, name: provider.name })}
                  className="text-xs font-bold text-emerald-500 flex items-center gap-1 hover:text-emerald-400"
                >
                  <UserPlus className="w-3 h-3 text-emerald-500" /> Adicionar Colaborador
                </button>
              </div>

              <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
                {provider.employees?.map(employee => (
                  <div 
                    key={employee.id} 
                    onClick={() => onViewEmployee(employee)}
                    className="bg-slate-900 p-4 rounded-xl border border-slate-800 hover:border-emerald-500/50 hover:shadow-2xl transition-all cursor-pointer group"
                  >
                    <div className="flex items-start justify-between mb-3">
                      <div>
                        <p className="font-bold text-white group-hover:text-emerald-400 transition-colors">{employee.name}</p>
                        <p className="text-[10px] text-slate-500 font-bold uppercase tracking-tight">{employee.role}</p>
                      </div>
                      <ChevronRight className="w-4 h-4 text-slate-600 group-hover:text-emerald-500 transition-colors" />
                    </div>
                    <div className="flex flex-wrap gap-1.5">
                      {employee.documents?.map(doc => (
                        <span 
                          key={doc.id} 
                          className={`text-[9px] px-2 py-0.5 rounded-md border font-black uppercase tracking-tighter ${
                            doc.status === 'EXPIRED' ? 'bg-red-500/10 text-red-500 border-red-500/20' : 
                            doc.status === 'EXPIRING' ? 'bg-amber-500/10 text-amber-500 border-amber-500/20' : 
                            'bg-emerald-500/10 text-emerald-500 border-emerald-500/20'
                          }`}
                        >
                          {doc.type}
                        </span>
                      ))}
                    </div>
                  </div>
                ))}
                {(!provider.employees || provider.employees.length === 0) && (
                  <p className="text-xs text-slate-600 italic py-4">Nenhum funcionário cadastrado ainda.</p>
                )}
              </div>
            </div>

            {aiAnalysis[provider.id] && (
              <div className="m-6 p-5 bg-emerald-500/5 border border-emerald-500/20 rounded-2xl relative animate-in fade-in zoom-in-95 duration-300">
                <button onClick={() => setAiAnalysis(prev => {const n={...prev}; delete n[provider.id]; return n;})} className="absolute top-4 right-4 text-slate-500 hover:text-white transition-colors">
                  <Plus className="w-5 h-5 rotate-45 text-white" />
                </button>
                <div className="flex gap-4">
                  <div className="bg-emerald-500/20 p-2 rounded-lg h-fit">
                    <ShieldAlert className="w-6 h-6 text-emerald-400" />
                  </div>
                  <div>
                    <h5 className="text-xs font-black text-emerald-400 mb-2 uppercase tracking-widest">Parecer Gemini AI - Caieiras</h5>
                    <p className="text-sm text-slate-300 leading-relaxed italic">"{aiAnalysis[provider.id]}"</p>
                  </div>
                </div>
              </div>
            )}
          </div>
        )) : (
          <div className="text-center py-20 bg-slate-900 rounded-3xl border border-dashed border-slate-800">
            <p className="text-slate-500 font-medium">Nenhum fornecedor cadastrado na base de dados.</p>
            <button onClick={onAddProvider} className="text-emerald-500 font-bold mt-2 hover:underline">Cadastrar o primeiro agora</button>
          </div>
        )}
      </div>

      <AddEmployeeModal 
        isOpen={!!activeAddEmployee}
        providerId={activeAddEmployee?.id || ''}
        providerName={activeAddEmployee?.name || ''}
        onClose={() => setActiveAddEmployee(null)}
        onSuccess={() => {
          setActiveAddEmployee(null);
          onRefresh();
        }}
      />
    </div>
  );
};

export default ProviderList;

import React, { useState, useEffect } from 'react';
import Layout from './components/Layout';
import Dashboard from './components/Dashboard';
import ProviderList from './components/ProviderList';
import EmployeeModal from './components/EmployeeModal';
import AddProviderModal from './components/AddProviderModal';
import UserAdmin from './components/UserAdmin';
import Settings from './components/Settings';
import Login from './components/Login';
import { dbService } from './services/dbService';
import { Provider, Employee, Document } from './types';
import { Loader2, RefreshCcw, DatabaseZap, Mail } from 'lucide-react';

const App: React.FC = () => {
  const [isLoggedIn, setIsLoggedIn] = useState(() => localStorage.getItem('bio_auth') === 'true');
  const [userEmail, setUserEmail] = useState(() => localStorage.getItem('bio_user') || '');
  const [activeTab, setActiveTab] = useState('dashboard');
  const [providers, setProviders] = useState<Provider[]>([]);
  const [selectedEmployeeId, setSelectedEmployeeId] = useState<string | null>(null);
  const [isAddProviderOpen, setIsAddProviderOpen] = useState(false);
  const [providerToEdit, setProviderToEdit] = useState<Provider | null>(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);

  const fetchData = async () => {
    if (!isLoggedIn) return;
    try {
      setLoading(true);
      setError(null);
      const data = await dbService.getFullProvidersData();
      setProviders(data);
    } catch (err: any) {
      setError("Falha ao conectar com o Supabase. Verifique as tabelas ou a rede.");
      console.error(err);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchData();
  }, [isLoggedIn]);

  const handleLogin = (email: string) => {
    setIsLoggedIn(true);
    setUserEmail(email);
    localStorage.setItem('bio_auth', 'true');
    localStorage.setItem('bio_user', email);
  };

  const handleLogout = () => {
    setIsLoggedIn(false);
    setUserEmail('');
    localStorage.removeItem('bio_auth');
    localStorage.removeItem('bio_user');
  };

  const handleEditProvider = (provider: Provider) => {
    setProviderToEdit(provider);
    setIsAddProviderOpen(true);
  };

  const handleCloseProviderModal = () => {
    setIsAddProviderOpen(false);
    setProviderToEdit(null);
  };

  const handleNotifyCompany = (provider: Provider, employee: Employee, doc: Document) => {
    const subject = encodeURIComponent(`ALERTA DE SEGURANÇA: Documentação Vencida - ${employee.name}`);
    const body = encodeURIComponent(
      `Prezada equipe da ${provider.name},\n\n` +
      `Identificamos através da plataforma BioSafety que o documento "${doc.type}" do colaborador ${employee.name} (CPF: ${employee.cpf}) VENCEU em ${new Date(doc.expiryDate).toLocaleDateString('pt-BR')}.\n\n` +
      `Para garantir a continuidade das operações na Usina de Biometano Caieiras, solicitamos que o colaborador realize a reciclagem/atualização necessária e que o novo certificado seja enviado imediatamente para regularização do acesso.\n\n` +
      `Atenciosamente,\n` +
      `Departamento de Segurança do Trabalho - BioSafety Caieiras`
    );

    window.location.href = `mailto:${provider.contactEmail}?subject=${subject}&body=${body}`;
  };

  const selectedEmployee = React.useMemo(() => {
    if (!selectedEmployeeId) return null;
    for (const p of providers) {
      const emp = p.employees?.find(e => e.id === selectedEmployeeId);
      if (emp) return emp;
    }
    return null;
  }, [selectedEmployeeId, providers]);

  const handleRefresh = () => fetchData();

  if (!isLoggedIn) {
    return <Login onLogin={handleLogin} />;
  }

  const renderContent = () => {
    if (loading && activeTab !== 'settings') {
      return (
        <div className="flex flex-col items-center justify-center min-h-[400px] text-emerald-500">
          <Loader2 className="w-12 h-12 animate-spin mb-4 text-emerald-500" />
          <p className="text-slate-400 font-bold uppercase tracking-widest text-xs animate-pulse">Sincronizando Dados...</p>
        </div>
      );
    }

    if (error) {
      return (
        <div className="flex flex-col items-center justify-center min-h-[400px] text-center p-12 bg-slate-900 rounded-[2.5rem] border border-red-900/20">
          <DatabaseZap className="w-16 h-16 text-red-500 mb-4 opacity-50" />
          <h2 className="text-xl font-black text-white mb-2 uppercase tracking-tight">Erro de Conexão</h2>
          <p className="text-slate-400 max-w-md mb-6 text-sm">{error}</p>
          <button onClick={fetchData} className="flex items-center gap-2 bg-slate-800 hover:bg-slate-700 text-white px-8 py-3 rounded-xl transition-all font-black text-xs uppercase tracking-widest">
            <RefreshCcw className="w-4 h-4 text-white" /> Tentar Novamente
          </button>
        </div>
      );
    }

    switch (activeTab) {
      case 'dashboard':
        return <Dashboard providers={providers} />;
      case 'providers':
        return (
          <ProviderList 
            providers={providers} 
            onAddProvider={() => setIsAddProviderOpen(true)}
            onEditProvider={handleEditProvider}
            onViewEmployee={(emp) => setSelectedEmployeeId(emp.id)}
            onRefresh={handleRefresh}
          />
        );
      case 'users':
        return <UserAdmin />;
      case 'settings':
        return <Settings />;
      case 'alerts':
        const expiredAlerts = providers.flatMap(p => 
          (p.employees || []).flatMap(e => 
            (e.documents || [])
              .filter(d => d.status === 'EXPIRED')
              .map(d => ({ provider: p, employee: e, doc: d }))
          )
        );

        return (
          <div className="bg-slate-900 p-12 rounded-[2.5rem] border border-slate-800 text-center space-y-6 animate-in fade-in duration-500">
            <div className="bg-red-500/10 w-24 h-24 rounded-full flex items-center justify-center mx-auto text-red-500 border border-red-500/20">
              <span className="text-4xl font-black text-white">!</span>
            </div>
            <div>
              <h2 className="text-3xl font-black text-white uppercase tracking-tight">Alertas de Segurança</h2>
              <p className="text-slate-500 mt-2 font-medium">Documentos expirados que exigem atenção imediata do Técnico.</p>
            </div>

            <div className="pt-8 grid gap-4 max-w-4xl mx-auto">
              {expiredAlerts.length > 0 ? (
                expiredAlerts.map((alert, i) => (
                  <div key={i} className="flex flex-col md:flex-row items-center justify-between p-6 bg-slate-950 border border-red-900/20 rounded-2xl text-left group hover:border-red-500/40 transition-all gap-4">
                    <div className="flex-1">
                      <p className="font-black text-red-400 uppercase text-[10px] tracking-widest">{alert.doc.type} EXPIRADO</p>
                      <p className="text-slate-200 font-bold text-lg mt-1">{alert.employee.name}</p>
                      <p className="text-xs text-slate-500 font-bold uppercase tracking-tight">{alert.provider.name} • Vencimento: {new Date(alert.doc.expiryDate).toLocaleDateString('pt-BR')}</p>
                    </div>
                    <button 
                      onClick={() => handleNotifyCompany(alert.provider, alert.employee, alert.doc)}
                      className="w-full md:w-auto flex items-center justify-center gap-2 bg-red-600 hover:bg-red-500 text-white px-6 py-3 rounded-xl text-[10px] font-black uppercase tracking-widest transition-all shadow-lg shadow-red-900/20 active:scale-95"
                    >
                      <Mail className="w-4 h-4 text-white" />
                      Notificar Empresa via E-mail
                    </button>
                  </div>
                ))
              ) : (
                <div className="text-emerald-500 bg-emerald-500/5 p-12 rounded-[2rem] border border-emerald-500/20 font-black uppercase tracking-widest text-sm">
                  Protocolo em conformidade. Nenhum vencimento crítico detectado.
                </div>
              )}
            </div>
          </div>
        );
      default:
        return null;
    }
  };

  return (
    <Layout activeTab={activeTab} setActiveTab={setActiveTab} onLogout={handleLogout} userEmail={userEmail}>
      {renderContent()}

      {selectedEmployeeId && (
        <EmployeeModal 
          employee={selectedEmployee} 
          onClose={() => setSelectedEmployeeId(null)} 
          onRefresh={handleRefresh}
        />
      )}

      <AddProviderModal 
        isOpen={isAddProviderOpen}
        providerToEdit={providerToEdit}
        onClose={handleCloseProviderModal}
        onSuccess={() => {
          handleCloseProviderModal();
          handleRefresh();
        }}
      />
    </Layout>
  );
};

export default App;

