interface DashboardProps {
  providers: Provider[];
}

const SAFETY_TIPS = [
  {
    title: "Risco de H2S",
    content: "O Gás Sulfídrico (H2S) é altamente tóxico e incolor. Certifique-se de que todos os terceiros portem detectores de quatro gases calibrados.",
