    footer: "Segurança NR-13"
  },
  {
    title: "Trabalho Quente",
    content: "Soldagens e cortes exigem isolamento da área e monitoramento de metano (CH4) em tempo real para evitar ignição acidental.",
    footer: "Prevenção de Incêndio"
  }
];

const Dashboard: React.FC<DashboardProps> = ({ providers }) => {
  const [currentTip, setCurrentTip] = useState(SAFETY_TIPS[0]);
  const [alertPeriod, setAlertPeriod] = useState(30);

  useEffect(() => {
    const randomIndex = Math.floor(Math.random() * SAFETY_TIPS.length);
    setCurrentTip(SAFETY_TIPS[randomIndex]);

    const saved = localStorage.getItem('biosafety_alert_period');
    if (saved) setAlertPeriod(parseInt(saved, 10));
  }, []);

  const stats = React.useMemo(() => {
    let totalEmployees = 0;
    let expired = 0;
    let expiring = 0;

    providers.forEach(p => {
      p.employees.forEach(e => {
        totalEmployees++;
        e.documents.forEach(d => {
          if (d.status === 'EXPIRED') expired++;
          if (d.status === 'EXPIRING') expiring++;
        });
      });
    });

    return { totalEmployees, expired, expiring };
  }, [providers]);

  const cards = [
    { label: 'Fornecedores', value: providers.length, icon: Factory, color: 'text-blue-400', bg: 'bg-blue-500/10' },
    { label: 'Colaboradores', value: stats.totalEmployees, icon: Users, color: 'text-emerald-400', bg: 'bg-emerald-500/10' },
    { label: 'Docs Vencidos', value: stats.expired, icon: FileX, color: 'text-red-400', bg: 'bg-red-500/10' },
    { label: `A Vencer (${alertPeriod}d)`, value: stats.expiring, icon: Clock, color: 'text-amber-400', bg: 'bg-amber-500/10' },
  ];

  return (
    <div className="space-y-8 animate-in fade-in slide-in-from-bottom-4 duration-500">
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
        {cards.map((card, i) => {
          const Icon = card.icon;
          return (
            <div key={i} className="bg-slate-900 p-6 rounded-2xl border border-slate-800 shadow-xl hover:border-slate-700 transition-all group">
              <div className="flex items-center justify-between mb-4">
                <div className={`${card.bg} p-3 rounded-xl transition-transform group-hover:scale-110`}>
                  <Icon className={`w-6 h-6 ${card.color}`} />
                </div>
                <div className="text-emerald-500 bg-emerald-500/10 px-2 py-1 rounded text-[10px] font-bold flex items-center gap-1">
                  <TrendingUp className="w-3 h-3" />
                  ATIVO
                </div>
              </div>
              <h3 className="text-slate-500 text-xs font-bold uppercase tracking-wider">{card.label}</h3>
              <p className="text-3xl font-bold text-white mt-1">{card.value}</p>
            </div>
          );
        })}
      </div>

      <div className="grid grid-cols-1 lg:grid-cols-3 gap-8">
        <div className="lg:col-span-2 bg-slate-900 rounded-2xl border border-slate-800 p-6 shadow-xl">
          <h3 className="text-lg font-bold mb-6 flex items-center gap-3 text-slate-100">
            <CheckCircle2 className="w-5 h-5 text-emerald-500" />
            Análise de Risco por Empresa
          </h3>
          <div className="space-y-4">
            {providers.map(p => {
              const expiredCount = p.employees.reduce((acc, emp) => acc + emp.documents.filter(d => d.status === 'EXPIRED').length, 0);
              return (
                <div key={p.id} className="flex items-center justify-between p-4 rounded-xl border border-slate-800 hover:bg-slate-800/50 transition-colors">
                  <div>
                    <p className="font-bold text-slate-200">{p.name}</p>
                    <p className="text-[10px] text-slate-500 font-bold uppercase tracking-tight">{p.cnpj}</p>
                  </div>
                  <div className="flex items-center gap-6">
                    <div className="text-right">
                      <p className={`text-xs font-bold ${expiredCount > 0 ? 'text-red-400' : 'text-emerald-400'}`}>
                        {expiredCount} pendências
                      </p>
                      <div className="w-32 h-1.5 bg-slate-800 rounded-full mt-2 overflow-hidden">
                        <div 
                          className={`h-full transition-all duration-1000 ${expiredCount > 0 ? 'bg-red-500' : 'bg-emerald-500'}`} 
                          style={{ width: `${Math.min(100, (expiredCount / 5) * 100 + 10)}%` }}
                        ></div>
                      </div>
                    </div>
                  </div>
                </div>
              );
            })}
          </div>
        </div>

        <div className="bg-gradient-to-br from-emerald-900 to-slate-900 rounded-2xl p-6 text-white shadow-2xl border border-emerald-800/30 flex flex-col justify-between">
          <div>
            <div className="flex items-center gap-2 mb-4 text-emerald-400">
              <Zap className="w-5 h-5 fill-current" />
              <h3 className="text-sm font-bold uppercase tracking-widest">Segurança de Biogás</h3>
            </div>
            <p className="text-emerald-50/80 text-sm leading-relaxed font-medium">
              Lembrete Crítico: {currentTip.content}
            </p>
          </div>
          <div className="mt-8 bg-black/30 p-4 rounded-xl border border-white/10 backdrop-blur-sm flex items-center gap-3">
            <div className="bg-emerald-500/20 p-2 rounded-lg">
              <ShieldAlert className="w-4 h-4 text-emerald-400" />
            </div>
            <div>
              <p className="text-[10px] uppercase font-black text-emerald-400 tracking-widest">{currentTip.title}</p>
              <p className="text-[9px] mt-0.5 text-slate-400 uppercase font-bold tracking-tight">{currentTip.footer}</p>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Dashboard;

import React, { useState } from 'react';
import { Plus, Search, ChevronRight, UserPlus, Bot, ShieldCheck, Edit2, Trash2, Loader2, ClipboardCheck, XCircle } from 'lucide-react';
import { Provider, Employee } from '../types';
import { analyzeCompliance } from '../services/geminiService';
import { dbService } from '../services/dbService';
import AddEmployeeModal from './AddEmployeeModal';

interface ProviderListProps {
  providers: Provider[];
  onAddProvider: () => void;
  onEditProvider: (provider: Provider) => void;
  onViewEmployee: (emp: Employee) => void;
  onEditEmployee: (providerId: string, providerName: string, employee: Employee) => void;
  onRefresh: () => void;
}

const ProviderList: React.FC<ProviderListProps> = ({ providers, onAddProvider, onEditProvider, onViewEmployee, onEditEmployee, onRefresh }) => {
  const [searchTerm, setSearchTerm] = useState('');
  const [aiAnalysis, setAiAnalysis] = useState<Record<string, string>>({});
  const [analyzingIds, setAnalyzingIds] = useState<Set<string>>(new Set());
  const [deletingProviderId, setDeletingProviderId] = useState<string | null>(null);
  const [deletingEmployeeId, setDeletingEmployeeId] = useState<string | null>(null);
  const [activeAddEmployee, setActiveAddEmployee] = useState<{id: string, name: string} | null>(null);

  // Lógica de Busca Profunda (Deep Scan)
  const filteredProviders = providers.filter(p => {
    const term = searchTerm.toLowerCase().trim();
    if (!term) return true;

    // 1. Dados da Prestadora
    const providerMatches = 
      p.name?.toLowerCase().includes(term) || 
      p.cnpj?.includes(term) || 
      p.contactEmail?.toLowerCase().includes(term);

    if (providerMatches) return true;

    // 2. Dados dos Colaboradores e seus Documentos
    const employeeMatches = p.employees?.some(e => {
      const basicInfoMatches = 
        e.name?.toLowerCase().includes(term) ||
        e.role?.toLowerCase().includes(term) ||
        e.cpf?.includes(term);

      if (basicInfoMatches) return true;

      // 3. Tipos de Certificados (NRs)
      return e.documents?.some(d => d.type?.toLowerCase().includes(term));
    });

    return employeeMatches;
  });

  const handleAiAnalysis = async (provider: Provider) => {
    setAnalyzingIds(prev => new Set(prev).add(provider.id));
    const dataStr = provider.employees?.map(e => `${e.name} (Função: ${e.role}): [${e.documents?.map(d => `${d.type} - ${d.status === 'VALID' ? 'Válido' : 'Vencido'}`).join(', ')}]`).join('; ') || 'Nenhum funcionário';
    const result = await analyzeCompliance(provider.name, dataStr);
    if (result) {
      setAiAnalysis(prev => ({ ...prev, [provider.id]: result }));
    }
    setAnalyzingIds(prev => {
      const next = new Set(prev);
      next.delete(provider.id);
      return next;
    });
  };

  const handleDeleteProvider = async (id: string, name: string) => {
    if (!confirm(`Deseja remover permanentemente o prestador ${name}?`)) return;
    setDeletingProviderId(id);
    try {
      await dbService.deleteProvider(id);
      onRefresh();
    } catch (err) {
      alert("Erro ao excluir prestador.");
    } finally {
      setDeletingProviderId(null);
    }
  };

  const handleDeleteEmployee = async (e: React.MouseEvent, id: string, name: string) => {
    e.stopPropagation();
    if (!confirm(`Deseja remover o colaborador ${name}?`)) return;
    setDeletingEmployeeId(id);
    try {
      await dbService.deleteEmployee(id);
      onRefresh();
    } catch (err) {
      alert("Erro ao excluir colaborador.");
    } finally {
      setDeletingEmployeeId(null);
    }
  };

  return (
    <div className="space-y-6">
      <div className="flex flex-col md:flex-row gap-4 items-center justify-between bg-slate-900 p-4 rounded-2xl border border-slate-800 shadow-xl">
        <div className="relative w-full md:w-[500px]">
          <Search className="absolute left-4 top-1/2 -translate-y-1/2 w-4 h-4 text-slate-400" />
          <input 
            type="text"
            placeholder="Pesquise por empresa, colaborador, CPF, função ou NR..."
            className="w-full pl-12 pr-12 py-3 bg-slate-950 border border-slate-800 rounded-xl focus:ring-2 focus:ring-emerald-600 focus:outline-none transition-all text-sm text-slate-200 placeholder:text-slate-600"
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
          />
          {searchTerm && (
            <button 
              onClick={() => setSearchTerm('')}
              className="absolute right-4 top-1/2 -translate-y-1/2 text-slate-500 hover:text-white transition-colors"
            >
              <XCircle className="w-4 h-4" />
            </button>
          )}
        </div>
        <button 
          onClick={onAddProvider}
          className="w-full md:w-auto flex items-center justify-center gap-2 bg-emerald-600 hover:bg-emerald-700 text-white px-6 py-3 rounded-xl font-bold text-sm transition-all shadow-lg shadow-emerald-900/20 active:scale-95"
        >
          <Plus className="w-4 h-4 text-white" />
          Nova Prestadora
        </button>
      </div>

      <div className="grid grid-cols-1 gap-8">
        {filteredProviders.length > 0 ? filteredProviders.map(provider => (
          <div key={provider.id} className="bg-slate-900 rounded-[2rem] border border-slate-800 shadow-2xl overflow-hidden animate-in fade-in slide-in-from-bottom-2">
            {/* Header do Prestador */}
            <div className="p-6 bg-slate-950/40 border-b border-slate-800 flex items-center justify-between">
              <div className="flex items-center gap-4">
                <div className="w-12 h-12 bg-emerald-600/10 rounded-xl flex items-center justify-center border border-emerald-500/20">
                  <span className="font-black text-emerald-500">{provider.name?.charAt(0)}</span>
                </div>
                <div>
                  <h3 className="text-lg font-black text-white">{provider.name}</h3>
                  <div className="flex items-center gap-3">
                    <p className="text-[10px] text-slate-500 font-bold uppercase tracking-widest">{provider.cnpj}</p>
                    <span className="w-1 h-1 bg-slate-800 rounded-full"></span>
                    <p className="text-[10px] text-slate-400 font-medium lowercase tracking-normal italic">{provider.contactEmail}</p>
                  </div>
                </div>
              </div>
              <div className="flex items-center gap-2">
                <button 
                  onClick={() => handleAiAnalysis(provider)}
                  disabled={analyzingIds.has(provider.id)}
                  className={`flex items-center gap-2 px-4 py-2 rounded-xl transition-all border ${analyzingIds.has(provider.id) ? 'bg-slate-800 text-slate-500 border-slate-700' : 'bg-emerald-500/10 text-emerald-400 border-emerald-500/20 hover:bg-emerald-500 hover:text-white'}`}
                >
                  {analyzingIds.has(provider.id) ? <Loader2 className="w-4 h-4 animate-spin text-white" /> : <Bot className="w-4 h-4 text-white" />}
                  <span className="text-[10px] font-black uppercase tracking-widest">Atribuições IA</span>
                </button>
                <button onClick={() => onEditProvider(provider)} className="p-2 text-white hover:bg-slate-800 rounded-lg transition-all border border-transparent hover:border-slate-700">
                  <Edit2 className="w-4 h-4 text-white" />
                </button>
                <button onClick={() => handleDeleteProvider(provider.id, provider.name)} className="p-2 text-white hover:text-red-500 hover:bg-red-500/10 rounded-lg transition-all">
                  <Trash2 className="w-4 h-4 text-white" />
                </button>
              </div>
            </div>

            {/* Quadro de Colaboradores */}
            <div className="p-6">
              <div className="flex items-center justify-between mb-4">
                <div className="flex items-center gap-2">
                  <ShieldCheck className="w-4 h-4 text-emerald-500" />
                  <h4 className="text-[10px] font-black text-slate-400 uppercase tracking-widest">Quadro de Atividades</h4>
                </div>
                <button 
                  onClick={() => setActiveAddEmployee({ id: provider.id, name: provider.name })}
                  className="text-xs font-bold text-emerald-500 flex items-center gap-1 hover:underline"
                >
                  <UserPlus className="w-3 h-3 text-emerald-500" /> Incluir Colaborador
                </button>
              </div>

              <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
                {provider.employees?.map(employee => {
                  // Destaca o card se houver match na busca
                  const isMatch = searchTerm && (
                    employee.name?.toLowerCase().includes(searchTerm.toLowerCase()) ||
                    employee.role?.toLowerCase().includes(searchTerm.toLowerCase()) ||
                    employee.cpf?.includes(searchTerm) ||
                    employee.documents?.some(d => d.type?.toLowerCase().includes(searchTerm.toLowerCase()))
                  );

                  return (
                    <div 
                      key={employee.id} 
                      onClick={() => onViewEmployee(employee)}
                      className={`p-5 rounded-2xl border transition-all cursor-pointer group relative ${
                        isMatch 
                        ? 'bg-emerald-500/10 border-emerald-500/50 shadow-[0_0_20px_rgba(16,185,129,0.1)]' 
                        : 'bg-slate-950/50 border-slate-800 hover:border-emerald-500/40'
                      }`}
                    >
                      <div className="flex justify-between items-start mb-4">
                        <div className="flex-1 pr-6">
                          <p className="font-black text-white group-hover:text-emerald-400 transition-colors truncate">{employee.name}</p>
                          <p className="text-[10px] text-slate-500 font-black uppercase mt-1 tracking-tighter">{employee.role}</p>
                          {searchTerm && employee.cpf?.includes(searchTerm) && (
                            <p className="text-[8px] text-emerald-400 font-bold mt-1">CPF Match: {employee.cpf}</p>
                          )}
                        </div>
                        <div className="flex gap-1 absolute top-4 right-4 opacity-0 group-hover:opacity-100 transition-opacity">
                          <button 
                            onClick={(e) => { e.stopPropagation(); onEditEmployee(provider.id, provider.name, employee); }}
                            className="p-1.5 bg-slate-800 hover:bg-emerald-600 rounded-lg border border-slate-700 text-white transition-all"
                          >
                            <Edit2 className="w-3.5 h-3.5 text-white" />
                          </button>
                          <button 
                            onClick={(e) => handleDeleteEmployee(e, employee.id, employee.name)}
                            className="p-1.5 bg-slate-800 hover:bg-red-600 rounded-lg border border-slate-700 text-white transition-all"
                          >
                            <Trash2 className="w-3.5 h-3.5 text-white" />
                          </button>
                        </div>
                      </div>

                      <div className="flex flex-wrap gap-1">
                        {employee.documents?.map(doc => {
                          const docMatch = searchTerm && doc.type?.toLowerCase().includes(searchTerm.toLowerCase());
                          return (
                            <span 
                              key={doc.id} 
                              className={`text-[8px] px-2 py-0.5 rounded-md font-black uppercase border ${
                                docMatch ? 'ring-1 ring-white' : ''
                              } ${
                                doc.status === 'EXPIRED' ? 'bg-red-500/10 text-red-500 border-red-500/20' : 'bg-emerald-500/10 text-emerald-500 border-emerald-500/20'
                              }`}
                            >
                              {doc.type}
                            </span>
                          );
                        })}
                      </div>
                    </div>
                  );
                })}
              </div>
            </div>

            {/* Análise Fixa de Atribuições IA */}
            {aiAnalysis[provider.id] && (
              <div className="m-6 mt-0 p-6 bg-slate-950 border border-emerald-500/20 rounded-[1.5rem] animate-in fade-in zoom-in-95 duration-500">
                <div className="flex items-center gap-3 mb-4 border-b border-emerald-500/10 pb-4">
                  <div className="bg-emerald-500/10 p-2 rounded-lg">
                    <ClipboardCheck className="w-5 h-5 text-emerald-400" />
                  </div>
                  <div>
                    <h5 className="text-[10px] font-black text-emerald-400 uppercase tracking-[0.2em]">Mapa de Atribuições Técnicas (IA)</h5>
                    <p className="text-[9px] text-slate-500 font-bold uppercase">Relatório de Permissões de Trabalho</p>
                  </div>
                </div>
                <div className="text-sm text-slate-300 leading-relaxed space-y-2 whitespace-pre-wrap font-medium italic">
                  {aiAnalysis[provider.id]}
                </div>
              </div>
            )}
          </div>
        )) : (
          <div className="text-center py-20 bg-slate-900 rounded-[2rem] border border-dashed border-slate-800">
            <p className="text-slate-500 font-bold uppercase tracking-widest text-xs">
              {searchTerm ? `Nenhum registro encontrado para "${searchTerm}"` : "Nenhum prestador encontrado."}
            </p>
            {searchTerm && (
              <button onClick={() => setSearchTerm('')} className="mt-4 text-emerald-500 text-xs font-black uppercase hover:underline">Limpar Filtros</button>
            )}
          </div>
        )}
      </div>

      <AddEmployeeModal 
        isOpen={!!activeAddEmployee}
        providerId={activeAddEmployee?.id || ''}
        providerName={activeAddEmployee?.name || ''}
        onClose={() => setActiveAddEmployee(null)}
        onSuccess={() => {
          setActiveAddEmployee(null);
          onRefresh();
        }}
      />
    </div>
  );
};

export default ProviderList;

import { GoogleGenAI, Type } from "@google/genai";

const getAiClient = () => {
  const apiKey = process.env.API_KEY;
  if (!apiKey) {
    throw new Error("API_KEY não configurada no ambiente.");
  }
  return new GoogleGenAI({ apiKey });
};

export const analyzeCompliance = async (providerName: string, employeesData: string) => {
  try {
    const ai = getAiClient();
    const response = await ai.models.generateContent({
      model: 'gemini-3-flash-preview',
      contents: `Analise técnica de campo para o prestador "${providerName}".
      Dados dos colaboradores e certificados: ${employeesData}. 

      INSTRUÇÕES CRÍTICAS DE FORMATO:
      1. NÃO inclua saudações como "Olá", "Prezados" ou "Como Coordenador...".
      2. NÃO comece com "Parecer técnico sobre..." ou "Submeto a análise...".
      3. Comece DIRETAMENTE pela listagem dos colaboradores.

      CONTEÚDO DA ANÁLISE:
      1. Para cada colaborador, determine as ATIVIDADES DE RISCO AUTORIZADAS (ex: Altura, Espaço Confinado, Elétrica) com base nas NRs válidas.
      2. Se houver um colaborador sem a NR essencial para sua função (ex: Eletricista sem NR-10), destaque como "IMPEDIMENTO CRÍTICO".

      Formato: Use tópicos curtos e objetivos.`,
      config: {
        systemInstruction: "Você é um motor de processamento de segurança do trabalho. Sua saída deve ser puramente técnica e direta, sem introduções, conclusões formais ou frases de cortesia.",
      }
    });
    return response.text;
  } catch (error) {
    console.error("Gemini Analysis Error:", error);
    return `Erro na análise técnica: ${error instanceof Error ? error.message : "Falha na comunicação com o servidor de IA."}`;
  }
};

export const suggestTrainingPlan = async (jobDescription: string) => {
  try {
    const ai = getAiClient();
    const response = await ai.models.generateContent({
      model: 'gemini-3-flash-preview',
      contents: `Com base na função: ${jobDescription}, liste as NRs obrigatórias para atuação em usina de biometano.`,
