import React, { useState, useEffect } from 'react';
import { Settings as SettingsIcon, Plus, Trash2, BookOpen, Loader2, Save, Clock, BellRing, ShieldAlert, Database, Copy, Check } from 'lucide-react';
import { dbService } from '../services/dbService';

const Settings: React.FC = () => {
  const [trainingTypes, setTrainingTypes] = useState<string[]>([]);
  const [loading, setLoading] = useState(true);
  const [newType, setNewType] = useState('');
  const [saving, setSaving] = useState(false);
  const [copied, setCopied] = useState(false);

  const [alertPeriod, setAlertPeriod] = useState(() => {
    const saved = localStorage.getItem('biosafety_alert_period');
    return saved ? parseInt(saved, 10) : 30;
  });
  const [savingPeriod, setSavingPeriod] = useState(false);

  const fetchSettings = async () => {
    try {
      setLoading(true);
      const types = await dbService.getTrainingTypes();
      setTrainingTypes(types);
    } catch (err) {
      console.error(err);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchSettings();
  }, []);

  const handleAdd = async (e: React.FormEvent) => {
    e.preventDefault();
    if (!newType.trim()) return;
    setSaving(true);
    try {
      await dbService.addTrainingType(newType.trim());
      setNewType('');
      await fetchSettings();
    } catch (err) {
      alert("Erro ao adicionar treinamento.");
    } finally {
      setSaving(false);
    }
  };

  const handleDelete = async (name: string) => {
    if (!confirm(`Deseja remover "${name}" da lista de opções oficiais?`)) return;
    try {
      await dbService.deleteTrainingType(name);
      await fetchSettings();
    } catch (err) {
      alert("Erro ao excluir.");
    }
  };

  const handleSavePeriod = () => {
    setSavingPeriod(true);
    localStorage.setItem('biosafety_alert_period', alertPeriod.toString());
    setTimeout(() => {
      setSavingPeriod(false);
    }, 800);
  };

  const sqlScript = `-- 1. Tabela de Usuários do Sistema
CREATE TABLE IF NOT EXISTS app_users (
    id UUID DEFAULT gen_random_uuid() PRIMARY KEY,
    name TEXT NOT NULL,
    email TEXT UNIQUE NOT NULL,
    password TEXT NOT NULL,
    role TEXT DEFAULT 'OPERATOR',
    created_at TIMESTAMPTZ DEFAULT NOW()
);

-- 2. Tabela de Prestadores de Serviço
CREATE TABLE IF NOT EXISTS providers (
    id UUID DEFAULT gen_random_uuid() PRIMARY KEY,
    name TEXT NOT NULL,
    cnpj TEXT UNIQUE NOT NULL,
    contact_email TEXT NOT NULL,
    status TEXT DEFAULT 'ACTIVE',
    created_at TIMESTAMPTZ DEFAULT NOW()
);

-- 3. Tabela de Colaboradores
CREATE TABLE IF NOT EXISTS employees (
    id UUID DEFAULT gen_random_uuid() PRIMARY KEY,
    provider_id UUID REFERENCES providers(id) ON DELETE CASCADE,
    name TEXT NOT NULL,
    cpf TEXT UNIQUE NOT NULL,
    role TEXT NOT NULL,
    created_at TIMESTAMPTZ DEFAULT NOW()
);

-- 4. Tabela de Documentos
