const InfoTooltip: React.FC<{ title: string; content: string; type?: 'basic' | 'deep' }> = ({ title, content, type = 'basic' }) => {
  const isDeep = type === 'deep';
  return (
    <div className="group relative inline-block ml-1.5 align-middle">
      {isDeep ? (
        <Microscope className="w-3.5 h-3.5 text-blue-500 hover:text-blue-400 cursor-help transition-colors" />
      ) : (
        <Info className="w-3.5 h-3.5 text-slate-600 hover:text-emerald-400 cursor-help transition-colors" />
      )}
      <div className={`absolute bottom-full left-1/2 -translate-x-1/2 mb-2 w-72 p-4 rounded-xl shadow-[0_20px_50px_rgba(0,0,0,0.5)] opacity-0 invisible group-hover:opacity-100 group-hover:visible transition-all z-50 pointer-events-none border ${
        isDeep ? 'bg-slate-900 border-blue-500/30' : 'bg-slate-800 border-slate-700'
      }`}>
        <div className="flex items-center gap-2 mb-2">
          {isDeep && <Binary className="w-3 h-3 text-blue-400" />}
          <p className={`text-[10px] font-black uppercase tracking-widest ${isDeep ? 'text-blue-400' : 'text-emerald-500'}`}>
            {title}
          </p>
        </div>
        <p className="text-[11px] text-slate-300 leading-relaxed font-medium">
          {content}
        </p>
        <div className={`absolute top-full left-1/2 -translate-x-1/2 border-8 border-transparent ${
          isDeep ? 'border-t-slate-900' : 'border-t-slate-800'
        }`}></div>
      </div>
    </div>
  );
};

const ExecutiveView: React.FC<ExecutiveViewProps> = ({ providers }) => {
  const executiveStats = useMemo(() => {
    let totalScore = 0;
    let blockedCount = 0;
    let criticalAlerts = 0;
    let totalEmployees = 0;

    const providersWithScore = providers.map(p => {
      let score = 100;
      let isBlocked = false;
      const criticalDocs = ['NR-33', 'NR-35', 'NR-10', 'NR-20', 'ASO'];

      (p.employees || []).forEach(emp => {
        totalEmployees++;
        const empDocs = emp.documents || [];

        criticalDocs.forEach(type => {
          const doc = empDocs.find(d => d.type === type);
          if (!doc) {
            score -= 10;
          } else if (doc.status === 'EXPIRED') {
            if (type === 'ASO') score -= 15;
            else score -= 20;
            isBlocked = true;
            criticalAlerts++;
          }
        });
      });

      score = Math.max(0, score);
      totalScore += score;
      if (isBlocked || score < 60) blockedCount++;

      return { ...p, score, isBlocked };
    });

    const avgScore = providers.length > 0 ? totalScore / providers.length : 100;

    return {
      avgScore,
      blockedCount,
      criticalAlerts,
      totalEmployees,
      providersWithScore
    };
  }, [providers]);

  const kpiInfo = {
    'Conformidade Geral': {
      basic: 'Média ponderada do Score QSSMA de todas as empresas. Reflete o nível de segurança documental.',
      deep: 'Fórmula: Σ(Score_Individual) / N_Prestadoras. O cálculo ignora empresas sem funcionários ativos para não distorcer a média por inatividade.'
    },
    'Prestadoras Bloqueadas': {
      basic: 'Empresas impedidas de operar devido a falhas críticas de segurança.',
      deep: 'Critérios: Score < 60 (Risco Sistêmico) OU Presença de Documento Crítico Expirado (NR-10/20/33/35 ou ASO). O desbloqueio requer 100% de regularização.'
    },
    'Alertas Críticos': {
      basic: 'Documentos vencidos de alto risco.',
      deep: 'Filtro: Documentos com Status=EXPIRED pertencentes ao conjunto impeditivo. Multiplicador de gravidade: 2.0x sobre o impacto de risco da unidade.'
    },
    'Horas Bloqueadas (Est.)': {
      basic: 'Impacto operacional estimado pela paralisação de frentes de trabalho.',
      deep: 'Cálculo: (N_Colaboradores_Bloqueados * Horas_Turno) * Fator_Ociosidade(0.85). Mede a ineficiência causada pelo descumprimento de QSSMA.'
    }
  };

  const kpis = [
    { label: 'Conformidade Geral', value: `${Math.round(executiveStats.avgScore)}%`, icon: Activity, color: 'text-emerald-500', bg: 'bg-emerald-500/10' },
    { label: 'Prestadoras Bloqueadas', value: executiveStats.blockedCount, icon: Lock, color: 'text-red-500', bg: 'bg-red-500/10' },
    { label: 'Alertas Críticos', value: executiveStats.criticalAlerts, icon: AlertOctagon, color: 'text-orange-500', bg: 'bg-orange-500/10' },
    { label: 'Horas Bloqueadas (Est.)', value: '42h', icon: History, color: 'text-blue-500', bg: 'bg-blue-500/10' },
  ];

  return (
    <div className="space-y-8 animate-in fade-in duration-500">
      {/* KPI Cards */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
        {kpis.map((kpi, i) => {
          const Icon = kpi.icon;
          const info = kpiInfo[kpi.label as keyof typeof kpiInfo];
          return (
            <div key={i} className="bg-slate-900 p-6 rounded-2xl border border-slate-800 shadow-xl group">
              <div className="flex items-center justify-between mb-4">
                <div className={`${kpi.bg} p-3 rounded-xl transition-transform group-hover:scale-110`}>
                  <Icon className={`w-6 h-6 ${kpi.color}`} />
                </div>
                {i === 0 && (
                  <div className="text-emerald-500 bg-emerald-500/10 px-2 py-1 rounded text-[8px] font-black flex items-center gap-1">
                    <TrendingUp className="w-3 h-3" /> +2.4%
                  </div>
                )}
              </div>
              <div className="flex items-center">
                <p className="text-slate-500 text-[10px] font-black uppercase tracking-widest leading-none">{kpi.label}</p>
                <div className="flex items-center">
                  <InfoTooltip title="Conceito" content={info.basic} />
                  <InfoTooltip title="Deep Analysis" content={info.deep} type="deep" />
                </div>
              </div>
              <h3 className="text-3xl font-bold text-white mt-2 tracking-tight">{kpi.value}</h3>
            </div>
