import React, { useMemo, useState } from 'react';
import { 
  ShieldCheck, 
  ShieldAlert, 
  TrendingUp, 
  Activity, 
  AlertOctagon, 
  Factory, 
  History, 
  Lock, 
  Unlock, 
  Info, 
  Microscope, 
  Binary,
  ChevronRight
} from 'lucide-react';
import { Provider } from '../types';

interface ExecutiveViewProps {
  providers: Provider[];
}

const InteractiveTooltip: React.FC<{ 
  title: string; 
  content: string; 
  deepContent?: string;
  type?: 'basic' | 'deep' 
}> = ({ title, content, deepContent, type = 'basic' }) => {
  const [isClicked, setIsClicked] = useState(false);
  const isDeep = type === 'deep';

  return (
    <div className="relative inline-block ml-1.5 align-middle">
      <button
        type="button"
        onClick={() => isDeep && setIsClicked(!isClicked)}
        className={`relative z-20 p-0.5 rounded-md transition-all duration-300 outline-none ${
          isClicked && isDeep ? 'bg-blue-500/20 text-blue-400 scale-110 shadow-[0_0_10px_rgba(59,130,246,0.5)]' : 
          'text-slate-600 hover:text-emerald-400 group-hover:text-slate-400'
        }`}
      >
        {isDeep ? (
          <Microscope className={`w-3.5 h-3.5 transition-colors ${isClicked ? 'text-blue-400' : 'hover:text-blue-400'}`} />
        ) : (
          <Info className="w-3.5 h-3.5 hover:text-emerald-400" />
        )}

        {/* Tooltip Content */}
        <div className={`absolute bottom-full left-1/2 -translate-x-1/2 mb-2 w-72 p-4 rounded-xl shadow-[0_20px_50px_rgba(0,0,0,0.6)] transition-all duration-200 pointer-events-none border opacity-0 invisible group-hover:opacity-100 group-hover:visible hover:opacity-100 hover:visible ${
          isClicked && isDeep ? 'opacity-100 visible bg-slate-900 border-blue-500/50' : 
          isDeep ? 'bg-slate-900 border-blue-500/20' : 'bg-slate-800 border-slate-700'
        }`}>
          <div className="flex items-center justify-between mb-2">
            <div className="flex items-center gap-2">
              {isDeep && <Binary className="w-3 h-3 text-blue-400" />}
              <p className={`text-[10px] font-black uppercase tracking-widest ${isDeep ? 'text-blue-400' : 'text-emerald-500'}`}>
                {isClicked && isDeep ? 'CÁLCULO ALGORÍTMICO' : title}
              </p>
            </div>
            {isDeep && !isClicked && <span className="text-[8px] bg-blue-500/20 text-blue-400 px-1.5 py-0.5 rounded font-black animate-pulse">CLIQUE</span>}
          </div>

          <p className="text-[11px] text-slate-300 leading-relaxed font-medium text-left">
            {isDeep ? (isClicked ? deepContent : content) : content}
          </p>

