const InteractiveTooltip: React.FC<{ 
  title: string; 
  content: string; 
  deepContent?: string;
  type?: 'basic' | 'deep' 
}> = ({ title, content, deepContent, type = 'basic' }) => {
  const [isClicked, setIsClicked] = useState(false);
  const isDeep = type === 'deep';

  return (
    <div className="relative inline-block ml-1.5 align-middle">
      <div className="relative inline-block group/tt">
        <button
          type="button"
          onClick={(e) => {
            e.stopPropagation();
            if (isDeep) setIsClicked(!isClicked);
          }}
          className={`relative z-20 p-1 rounded-md transition-all duration-300 outline-none flex items-center justify-center ${
            isClicked && isDeep 
              ? 'bg-blue-500/20 text-blue-400 scale-110 shadow-[0_0_15px_rgba(59,130,246,0.5)]' 
              : 'text-slate-600 hover:text-emerald-400'
          }`}
        >
          {isDeep ? (
            <Microscope className={`w-3.5 h-3.5 transition-colors ${isClicked ? 'text-blue-400' : 'hover:text-blue-400'}`} />
          ) : (
            <Info className="w-3.5 h-3.5" />
          )}
        </button>

        {/* Tooltip Balloon */}
        <div className={`absolute bottom-full left-1/2 -translate-x-1/2 mb-3 w-72 p-4 rounded-2xl shadow-2xl transition-all duration-300 pointer-events-none border z-50 
          ${isDeep && isClicked 
            ? 'opacity-100 visible bg-slate-900 border-blue-500/50 translate-y-0 scale-100' 
            : 'opacity-0 invisible group-hover/tt:opacity-100 group-hover/tt:visible translate-y-1 scale-95'
          }
          ${isDeep && isClicked ? 'bg-slate-900' : isDeep ? 'bg-slate-900 border-blue-500/20' : 'bg-slate-800 border-slate-700'}
        `}>
          <div className="flex items-center justify-between mb-2">
            <div className="flex items-center gap-2">
              {isDeep && <Binary className={`w-3 h-3 ${isClicked ? 'text-blue-400' : 'text-blue-500/50'}`} />}
              <p className={`text-[10px] font-black uppercase tracking-widest ${isDeep ? (isClicked ? 'text-blue-400' : 'text-blue-500/50') : 'text-emerald-500'}`}>
                {isDeep && isClicked ? 'DETALHAMENTO TÉCNICO' : title}
              </p>
            </div>
            {isDeep && !isClicked && (
              <span className="text-[7px] bg-blue-500/20 text-blue-400 px-1.5 py-0.5 rounded font-black animate-pulse">CLIQUE PARA EXPANDIR</span>
            )}
          </div>

          <p className={`text-[11px] leading-relaxed font-medium text-left ${isDeep && isClicked ? 'text-blue-100' : 'text-slate-300'}`}>
            {isDeep ? (isClicked ? deepContent : content) : content}
          </p>

          <div className={`absolute top-full left-1/2 -translate-x-1/2 border-8 border-transparent ${
            isDeep && isClicked ? 'border-t-blue-500/50' : isDeep ? 'border-t-slate-900' : 'border-t-slate-800'
          }`}></div>
        </div>
      </div>
    </div>
  );
};

